import{_ as i,e as t}from"./index.8f841a57.js";const o="/images/blogs/feibuqilai.jpg",s={},e=t('<title>Drone sim</title><div class="container"><div class="content"><h1 id="%E9%9B%86%E7%BE%A4%E9%AA%8C%E8%AF%81">\u96C6\u7FA4\u9A8C\u8BC1</h1><p>\u6700\u8FD1\u7814\u7A76\u65E0\u4EBA\u673A\u96C6\u7FA4\u4EFF\u771F, \u7528 Pixhawk 2.4.8 \u505A\u786C\u4EF6\u5728\u73AF, QGC \u5730\u9762\u7AD9\u4E0E AirSim \u6A21\u62DF\u5668.</p><h2 id="%E8%A3%85%E7%8E%AF%E5%A2%83">\u88C5\u73AF\u5883</h2><p>\u6211\u4EEC\u9009\u62E9\u5728 Windows \u5E73\u53F0\u642D\u5EFA\u73AF\u5883, \u4E3B\u8981\u56FE gv100 \u663E\u5361\u7684\u9A71\u52A8\u7A33\u5B9A\u6027\u548C UE4 \u7684\u8F6F\u4EF6\u7A33\u5B9A\u6027.</p><p> Follow the <a href="https://microsoft.github.io/AirSim/px4_setup/">introduce</a>. </p><h3 id="%E8%BD%AF%E4%BB%B6%E5%9C%A8%E7%8E%AF">\u8F6F\u4EF6\u5728\u73AF</h3><p>\u5148\u5C1D\u8BD5\u5728 wsl \u91CC\u8DD1 PX4 \u8DD1\u901A\u518D\u8BF4\u786C\u4EF6;</p><ol><li>\u6CE8\u518C epic \u8D26\u53F7\u4E0B\u8F7D UE4;</li><li> \u7F16\u8BD1\u4E2A <a href="https://microsoft.github.io/AirSim/build_windows">AirSim \u63D2\u4EF6</a>; \u5B8C\u4E8B\u540E\u62D6\u8FDB ue4 \u7528. </li><li> \u4E0B\u8F7D <a href="https://docs.qgroundcontrol.com/master/en/getting_started/download_and_install.html">QGC</a></li><li>\u88C5\u4E2A<a href="https://github.com/Tencent/puerts">\u666E\u6D31 TS</a>, \u7528 cpp \u5199 ue \u5F00\u53D1\u4E0D\u5982\u5199 web \u7B80\u5355.</li><li> \u88C5\u4E2A<a href="https://github.com/Esri/vitruvio/blob/main/doc/installation.md">vitruvio</a> \u81EA\u52A8\u751F\u6210\u57CE\u5E02\u6A21\u578B, \u8FEB\u4E8E\u767E\u4EAC\u4E0D\u8BA9\u822A\u62CD\u626B\u6A21\u53EA\u80FD\u7528\u6B21\u4E0B\u4E0B\u7B56. </li><li>\u88C5 wsl, \u6700\u5F00\u59CB\u6D4B SIL \u7684\u65F6\u5019 px4 \u8FD8\u53EA\u80FD\u8DD1\u5728 wsl1 \u91CC, \u73B0\u5728\u597D\u50CF\u80FD\u5728 wsl2 \u91CC\u8FD0\u884C\u4E86, \u4E0D\u8FC7\u8FD9\u91CC\u8FD8\u662F\u7EE7\u7EED\u7528 1.</li><li>\u5728 wsl \u91CC\u88C5 px4,</li></ol><h3 id="%E7%A1%AC%E4%BB%B6%E5%9C%A8%E7%8E%AF">\u786C\u4EF6\u5728\u73AF</h3><p> \u8FEB\u4E8E\u8D2B\u7A77, \u53EA\u80FD\u7528\u505C\u4EA7\u7684 pixhawk 2.4.8 \u5E73\u53F0, \u51FA\u4E8E\u4E00\u4E9B\u5947\u5999\u6DF1\u523B\u7684\u539F\u56E0, \u8FD8\u4E0D\u80FD\u7528 px4 \u98DE\u63A7, \u88AB\u8FEB\u4E0B\u8F7D <a href="https://github.com/Firmament-Autopilot/FMT-Firmware">fmt</a> \u98DE\u63A7, \u8FD9\u4E0B\u5B8C\u5168\u56FD\u4EA7\u81EA\u4E3B\u7814\u53D1\u4E86. </p><h6 id="delps-%E4%B8%BA%E4%BB%80%E4%B9%88%E8%BF%99%E4%B8%AA%E9%A3%9E%E6%8E%A7%E7%9A%84%E5%90%8D%E5%AD%97%E5%8F%AB%E5%81%9A%E6%A0%BC%E5%BC%8F%E5%8C%96del"><del>ps: \u4E3A\u4EC0\u4E48\u8FD9\u4E2A\u98DE\u63A7\u7684\u540D\u5B57\u53EB\u505A\u683C\u5F0F\u5316</del></h6><hr><p> \u4E0B\u8F7D\u56FA\u4EF6\u5176\u5B9E\u662F\u5F88\u7B80\u5355\u7684, \u53EA\u9700\u8981\u5C06\u98DE\u63A7\u786C\u4EF6\u7528 usb \u8FDE\u63A5\u5230\u7535\u8111, \u6253\u5F00 QGC \u83DC\u5355\u9009\u62E9\u8F7D\u5177\u914D\u7F6E, \u91CC\u9762\u6709\u53EF\u89C6\u5316\u754C\u9762\u548C\u81EA\u52A8\u811A\u672C\u53BB\u5B8C\u6210\u4E0B\u8F7D. </p><p> \u4F46\u662F fmt \u6709\u4E00\u4E9B bug, \u4E0D\u77E5\u9053\u662F\u98DE\u63A7\u786C\u4EF6\u6454\u4E86, \u8FD8\u662F\u592A\u8FC7\u53E4\u8001\u7684\u8BBE\u5907\u4E0D\u652F\u6301, gps \u6A21\u5757, \u9AD8\u5EA6\u8BA1\u90FD\u662F\u51FA\u9519\u7684, \u4F3C\u4E4E\u53EA\u6709\u6C34\u5E73\u4EEA<strong>\u80FD\u591F</strong>\u5DE5\u4F5C (\u653E\u5728\u6C34\u5E73\u9762\u4E0A\u662F\u6B6A\u7684). </p><h2 id="%E8%B5%B7%E9%A3%9E">\u8D77\u98DE</h2><p> \u98DE\u63A7\u70B8\u4E86, qgc \u91CC\u6CA1\u529E\u6CD5\u6821\u51C6\u4F20\u611F\u5668, airsim \u91CC\u6CA1\u6CD5\u548C qgc \u901A\u8BAF. <img src="'+o+'" alt="\u98DE\u4E0D\u8D77\u6765"></p><h2 id="todo">TODO</h2><p>\u4E0B\u5468\u7EE7\u7EED\u641E\u8FD9\u4E2A</p></div></div>',2);function l(r,p){return e}const a=i(s,[["render",l]]);export{a as default};
