import{B as u}from"./BackToTopButton.4b5a936b.js";import{d as E,o as F,c as B,b as A,F as l,e}from"./index.f6e50d68.js";const a=e(`<title>Hadoop \u9762\u7ECF</title><h1 id="hadoop-%E5%85%A8%E5%AE%B6%E6%A1%B6">Hadoop \u5168\u5BB6\u6876</h1><p>\u76EE\u5F55</p><ol><li><a href="#hadoop-%E5%85%A8%E5%AE%B6%E6%A1%B6">Hadoop \u5168\u5BB6\u6876</a><ol><li><a href="#hadoop">Hadoop</a><ol><li><a href="#hadoop-%E5%B8%B8%E8%A7%81%E8%BE%93%E5%85%A5%E6%A0%BC%E5%BC%8F">Hadoop \u5E38\u89C1\u8F93\u5165\u683C\u5F0F</a></li><li><a href="#%E6%90%AD%E5%BB%BA-hadoop-%E9%9B%86%E7%BE%A4%E7%9A%84%E4%B8%BB%E8%A6%81%E9%85%8D%E7%BD%AE%E6%96%87%E4%BB%B6">\u642D\u5EFA Hadoop \u96C6\u7FA4\u7684\u4E3B\u8981\u914D\u7F6E\u6587\u4EF6</a></li><li><a href="#%E6%AD%A3%E5%B8%B8%E7%9A%84-hadoop-%E9%9B%86%E7%BE%A4%E8%BF%9B%E7%A8%8B%E4%B8%8E%E4%BD%9C%E7%94%A8">\u6B63\u5E38\u7684 Hadoop \u96C6\u7FA4\u8FDB\u7A0B\u4E0E\u4F5C\u7528</a></li><li><a href="#secondarynode-%E7%9A%84%E5%85%B7%E4%BD%93%E4%BD%9C%E7%94%A8">SecondaryNode \u7684\u5177\u4F53\u4F5C\u7528</a></li><li><a href="#namenode-%E5%87%BA%E7%8E%B0%E6%95%85%E9%9A%9C%E5%A6%82%E4%BD%95%E6%81%A2%E5%A4%8D">NameNode \u51FA\u73B0\u6545\u969C\u5982\u4F55\u6062\u590D</a></li><li><a href="#hadoop-%E7%9A%84-rack-awareness-%E6%98%AF%E4%BB%80%E4%B9%88">Hadoop \u7684 Rack Awareness \u662F\u4EC0\u4E48</a></li><li><a href="#hadoop-%E5%A4%A7%E7%89%88%E6%9C%AC%E5%8C%BA%E5%88%AB">Hadoop \u5927\u7248\u672C\u533A\u522B</a><ol><li><a href="#1x">1.x</a></li><li><a href="#2x">2.x</a></li><li><a href="#3x">3.x</a></li></ol></li><li><a href="#hadoop-%E9%AB%98%E5%8F%AF%E7%94%A8">Hadoop \u9AD8\u53EF\u7528</a></li><li><a href="#hadoop-%E9%85%8D%E7%BD%AE%E8%B0%83%E4%BC%98">Hadoop \u914D\u7F6E\u8C03\u4F18</a></li></ol></li><li><a href="#hdfs">HDFS</a><ol><li><a href="#hdfs-%E6%96%87%E4%BB%B6%E7%9A%84%E4%B8%80%E4%BA%9B%E6%A6%82%E5%BF%B5">HDFS \u6587\u4EF6\u7684\u4E00\u4E9B\u6982\u5FF5</a></li><li><a href="#hdfs-%E7%9A%84-block">HDFS \u7684 Block</a><ol><li><a href="#block-%E5%92%8C%E8%BE%93%E5%85%A5%E5%88%86%E5%89%B2%E7%9B%B4%E6%8E%A5%E6%9C%89%E4%BB%80%E4%B9%88%E5%8C%BA%E5%88%AB">Block \u548C\u8F93\u5165\u5206\u5272\u76F4\u63A5\u6709\u4EC0\u4E48\u533A\u522B</a></li><li><a href="#hdfs-%E7%9A%84-block-%E6%80%8E%E4%B9%88%E8%B0%83%E6%95%B4%E5%A4%A7%E5%B0%8F">HDFS \u7684 Block \u600E\u4E48\u8C03\u6574\u5927\u5C0F</a></li></ol></li><li><a href="#hdfs-%E5%AF%B9%E5%8D%95%E7%8B%AC%E4%B8%80%E4%B8%AA%E6%96%87%E4%BB%B6%E8%B0%83%E6%95%B4-block-%E5%A4%A7%E5%B0%8F">HDFS \u5BF9\u5355\u72EC\u4E00\u4E2A\u6587\u4EF6\u8C03\u6574 Block \u5927\u5C0F</a></li><li><a href="#block-%E5%89%AF%E6%9C%AC%E6%94%BE%E7%BD%AE%E7%AD%96%E7%95%A5">Block \u526F\u672C\u653E\u7F6E\u7B56\u7565</a><ol><li><a href="#1x-%E7%89%88%E6%9C%AC%E7%9A%84-hdfs">1.x \u7248\u672C\u7684 HDFS</a></li><li><a href="#2x-%E7%89%88%E6%9C%AC%E7%9A%84-hdfs">2.x \u7248\u672C\u7684 HDFS</a></li></ol></li><li><a href="#hdfs-%E5%86%99%E8%BF%87%E7%A8%8B">HDFS \u5199\u8FC7\u7A0B</a></li><li><a href="#hdfs-%E8%AF%BB%E8%BF%87%E7%A8%8B">HDFS \u8BFB\u8FC7\u7A0B</a></li><li><a href="#%E5%BE%80-hdfs-%E9%87%8C-put-%E6%96%87%E4%BB%B6%E6%97%B6-hdfs-%E9%83%BD%E5%81%9A%E4%BA%86%E4%BB%80%E4%B9%88">\u5F80 HDFS \u91CC put \u6587\u4EF6\u65F6 HDFS \u90FD\u505A\u4E86\u4EC0\u4E48</a></li></ol></li><li><a href="#mapreduce">MapReduce</a><ol><li><a href="#mapreduce-%E7%9A%84%E5%B7%A5%E4%BD%9C%E5%8E%9F%E7%90%86%E4%B8%8E%E6%B5%81%E7%A8%8B">MapReduce \u7684\u5DE5\u4F5C\u539F\u7406\u4E0E\u6D41\u7A0B</a></li><li><a href="#map-%E7%9A%84%E8%BF%90%E8%A1%8C%E6%AD%A5%E9%AA%A4">Map \u7684\u8FD0\u884C\u6B65\u9AA4</a></li><li><a href="#mapreduce-%E7%9A%84%E6%95%B0%E6%8D%AE%E5%80%BE%E6%96%9C">MapReduce \u7684\u6570\u636E\u503E\u659C</a></li><li><a href="#mapper-combiner-%E5%90%8E%E4%BC%9A%E5%8F%91%E7%94%9F%E4%BB%80%E4%B9%88">Mapper Combiner \u540E\u4F1A\u53D1\u751F\u4EC0\u4E48</a></li><li><a href="#map-%E8%BE%93%E5%87%BA%E7%9A%84%E6%95%B0%E6%8D%AE%E8%B6%85%E5%87%BA%E5%B0%8F%E6%96%87%E4%BB%B6%E5%86%85%E5%AD%98%E5%90%8E%E4%BC%9A%E5%8F%91%E7%94%9F%E4%BB%80%E4%B9%88">Map \u8F93\u51FA\u7684\u6570\u636E\u8D85\u51FA\u5C0F\u6587\u4EF6\u5185\u5B58\u540E\u4F1A\u53D1\u751F\u4EC0\u4E48</a></li><li><a href="#map-%E5%88%B0-reduce-%E9%BB%98%E8%AE%A4%E7%9A%84%E5%88%86%E5%8C%BA%E6%9C%BA%E5%88%B6">Map \u5230 Reduce \u9ED8\u8BA4\u7684\u5206\u533A\u673A\u5236</a></li><li><a href="#split-%E6%9C%BA%E5%88%B6">Split \u673A\u5236</a></li><li><a href="#%E4%B8%BA%E4%BB%80%E4%B9%88-split-%E4%B8%8D%E4%B8%8E-block-%E5%AF%B9%E5%BA%94">\u4E3A\u4EC0\u4E48 Split \u4E0D\u4E0E Block \u5BF9\u5E94</a></li><li><a href="#shuffle-%E5%8E%9F%E7%90%86%E4%B8%8E%E4%BC%98%E5%8C%96">Shuffle \u539F\u7406\u4E0E\u4F18\u5316</a></li></ol></li><li><a href="#yarn">YARN</a><ol><li><a href="#yarn-%E7%9A%84%E4%BC%98%E5%8A%BF">YARN \u7684\u4F18\u52BF</a></li><li><a href="#mapreduce-on-yarn-%E5%B7%A5%E4%BD%9C%E6%B5%81%E7%A8%8B">MapReduce on YARN \u5DE5\u4F5C\u6D41\u7A0B</a></li></ol></li></ol></li></ol><h2 id="hadoop">Hadoop</h2><h3 id="hadoop-%E5%B8%B8%E8%A7%81%E8%BE%93%E5%85%A5%E6%A0%BC%E5%BC%8F">Hadoop \u5E38\u89C1\u8F93\u5165\u683C\u5F0F</h3><ul><li>\u6587\u672C\u8F93\u5165: \u9ED8\u8BA4\u8F93\u5165\u683C\u5F0F.</li><li>\u5E8F\u5217\u6587\u4EF6\u8F93\u5165: \u8981\u8BFB\u53D6\u5E8F\u5217\u6587\u4EF6\u4E2D\u7684\u6587\u4EF6, \u9700\u8981\u4F7F\u7528\u5E8F\u5217\u6587\u4EF6\u8F93\u5165\u683C\u5F0F.</li><li>KV \u8F93\u5165: \u7528\u4E8E\u7EAF\u6587\u672C\u7684\u8F93\u5165.</li></ul><h3 id="%E6%90%AD%E5%BB%BA-hadoop-%E9%9B%86%E7%BE%A4%E7%9A%84%E4%B8%BB%E8%A6%81%E9%85%8D%E7%BD%AE%E6%96%87%E4%BB%B6"> \u642D\u5EFA Hadoop \u96C6\u7FA4\u7684\u4E3B\u8981\u914D\u7F6E\u6587\u4EF6 </h3><ul><li> core-site.xml <ul><li>\u7528\u4E8E\u5B9A\u4E49\u96C6\u7FA4\u5168\u5C40\u53C2\u6570, \u5982 HDFS URL, Hadoop \u4E34\u65F6\u76EE\u5F55\u7B49.</li></ul></li><li> hdfs-site.xml <ul><li>\u7528\u4E8E\u5B9A\u4E49 HDFS \u53C2\u6570, \u5982\u8282\u70B9\u540D\u79F0, \u6570\u636E\u8282\u70B9\u7684\u5B58\u653E\u4F4D\u7F6E, \u6587\u4EF6\u526F\u672C\u6570, \u6587\u4EF6\u8BFB\u53D6\u6743\u9650\u7B49.</li></ul></li><li> mapred-site.xml <ul><li> \u7528\u4E8E\u5B9A\u4E49 MapReduce \u53C2\u6570, \u5305\u62EC JobHistory Server \u548C\u5E94\u7528\u7A0B\u5E8F\u53C2\u6570\u4E24\u90E8\u5206, \u5982 Reduce \u4EFB\u52A1\u7684\u9ED8\u8BA4\u4E2A\u6570, \u4EFB\u52A1\u80FD\u591F\u4F7F\u7528\u7684\u5185\u5B58\u5927\u5C0F\u7B49. </li></ul></li><li> YARN-site.xml <ul><li>\u96C6\u7FA4\u8D44\u6E90\u7BA1\u7406\u5668\u7684\u914D\u7F6E, \u4F8B\u5982 ReSourceManager, NodeManager, Web \u76D1\u63A7\u7A0B\u5E8F\u7684\u7AEF\u53E3.</li></ul></li></ul><h3 id="%E6%AD%A3%E5%B8%B8%E7%9A%84-hadoop-%E9%9B%86%E7%BE%A4%E8%BF%9B%E7%A8%8B%E4%B8%8E%E4%BD%9C%E7%94%A8"> \u6B63\u5E38\u7684 Hadoop \u96C6\u7FA4\u8FDB\u7A0B\u4E0E\u4F5C\u7528 </h3><ul><li> NameNode <ul><li>\u4E3B\u8282\u70B9, \u8D1F\u8D23\u7EF4\u62A4\u6574\u4E2A HDFS \u6587\u4EF6\u7CFB\u7EDF\u7684\u76EE\u5F55\u6811, \u4EE5\u53CA\u6BCF\u4E2A\u6587\u4EF6\u6240\u5BF9\u5E94\u7684 Block \u5757\u4FE1\u606F (\u5143\u6570\u636E).</li></ul></li><li> DataNode <ul><li>\u4ECE\u8282\u70B9, \u8D1F\u8D23\u5B58\u50A8\u5177\u4F53\u7684\u6587\u4EF6\u6570\u636E, \u5E76\u4E14\u6BCF\u4E2A Block \u53EF\u4EE5\u5728\u591A\u4E2A DataNode \u4E0A\u5B58\u50A8\u591A\u4E2A\u526F\u672C.</li></ul></li><li> Secondary NameNode <ul><li> \u76F8\u5F53\u4E8E\u4E00\u4E2A\u5907\u7528\u7684 NameNode, \u5F53 NameNode \u4E0B\u7EBF\u4E4B\u540E, \u53EF\u4EE5\u5C06 Secondary NameNode \u7684\u6570\u636E\u5907\u4EFD\u5230 NameNode \u4E0A\u9762, \u4F46\u4E0D\u80FD\u5907\u4EFD\u5B8C\u6574\u6570\u636E </li><li>\u4E3B\u8981\u8D1F\u8D23\u955C\u50CF\u5907\u4EFD, \u65E5\u5FD7\u4E0E\u955C\u50CF\u5B9A\u671F\u5408\u5E76.</li></ul></li></ul><h3 id="secondarynode-%E7%9A%84%E5%85%B7%E4%BD%93%E4%BD%9C%E7%94%A8">SecondaryNode \u7684\u5177\u4F53\u4F5C\u7528</h3><p>Secondary NameNode \u4F1A\u7ECF\u5E38\u5411 NameNode \u53D1\u9001\u8BF7\u6C42,\u662F\u5426\u6EE1\u8DB3 check.</p><p>\u5F53\u6761\u4EF6\u6EE1\u8DB3\u65F6, Secondary NameNode \u5C06\u8FDB\u884C CheckPoint .</p><p> \u8FD9\u65F6 NameNode \u6EDA\u52A8\u5F53\u524D\u6B63\u5728\u5199\u7684 Edits, \u5C06\u521A\u521A\u6EDA\u52A8\u6389\u7684\u548C\u4E4B\u524D Edits \u6587\u4EF6\u8FDB\u884C\u5408\u5E76. Secondary NameNode \u4E0B\u8F7D Edits \u6587\u4EF6, \u7136\u540E\u5C06 Edits \u6587\u4EF6\u548C\u81EA\u8EAB\u4FDD\u5B58\u7684 fsimage \u6587\u4EF6\u5728\u5185\u5B58\u4E2D\u8FDB\u884C\u5408\u5E76, \u7136\u540E\u5199\u5165\u78C1\u76D8\u5E76\u4E0A\u4F20\u65B0\u7684 fsimage \u5230 nameNode, \u8FD9\u65F6 NameNode \u5C06\u65E7\u7684 fsimage \u7528\u65B0\u7684\u66FF\u6362\u6389. </p><blockquote><p><code>hdfs haadmin -getServiceState namenode_name</code> \u67E5\u770B\u72B6\u6001.</p></blockquote><h3 id="namenode-%E5%87%BA%E7%8E%B0%E6%95%85%E9%9A%9C%E5%A6%82%E4%BD%95%E6%81%A2%E5%A4%8D"> NameNode \u51FA\u73B0\u6545\u969C\u5982\u4F55\u6062\u590D </h3><ol><li>\u4F7F\u7528\u6587\u4EF6\u7CFB\u7EDF\u5143\u6570\u636E\u526F\u672C FsImage \u542F\u52A8\u65B0\u7684 NameNode.</li><li>\u914D\u7F6E\u65B0\u7684\u6570\u636E\u8282\u70B9\u548C Client \u4F7F\u5176\u786E\u8BA4\u65B0\u542F\u52A8\u7684 NameNode \u8282\u70B9\u540D\u79F0.</li><li> \u4E00\u65E6\u65B0\u7684 NameNode \u5B8C\u6210\u52A0\u8F7D\u6700\u540E\u4E00\u4E2A\u4ECE DataNode \u63A5\u6536\u5230\u8DB3\u591F\u963B\u6B62\u62A5\u544A\u7684 CheckPoint FsImage, \u5B83\u5C06\u5F00\u59CB\u4E3A Client \u63D0\u4F9B\u670D\u52A1. </li></ol><h3 id="hadoop-%E7%9A%84-rack-awareness-%E6%98%AF%E4%BB%80%E4%B9%88">Hadoop \u7684 Rack Awareness \u662F\u4EC0\u4E48</h3><p>\u5E94\u7528\u4E8E NameNode \u7684\u7B97\u6CD5, \u7528\u4E8E\u786E\u5B9A\u5982\u4F55\u653E\u7F6E\u5757\u4E0E\u5176\u526F\u672C.</p><p>\u5B9A\u4E49: \u5728\u540C\u4E00\u673A\u67B6\u5185\u7684 DataNode \u4E4B\u95F4\u5C06\u7F51\u7EDC\u6D41\u91CF\u6700\u5C0F\u5316, \u6BD4\u5982 AB \u526F\u672C\u5728\u540C\u4E00\u673A\u67B6, C \u5728\u53E6\u4E00\u4E2A.</p><h3 id="hadoop-%E5%A4%A7%E7%89%88%E6%9C%AC%E5%8C%BA%E5%88%AB">Hadoop \u5927\u7248\u672C\u533A\u522B</h3><h4 id="1x">1.x</h4><p>\u7531 HDFS \u4E0E MapReduce \u7EC4\u6210.</p><ul><li>HDFS \u4F5C\u4E3A\u6587\u4EF6\u7CFB\u7EDF.</li><li>MapReduce \u8D1F\u8D23\u8BA1\u7B97\u548C\u8D44\u6E90\u8C03\u5EA6\u5DE5\u4F5C.</li><li>\u7531\u4E3B\u8282\u70B9 Jobtrack \u548C\u5B50\u8282\u70B9 Tasktrack \u7EC4\u6210.</li><li>Tasktrack \u8D1F\u8D23\u6267\u884C\u4EFB\u52A1, \u4EFB\u52A1\u7531 MapTask \u548C ReduceTask \u5B8C\u6210.</li></ul><h4 id="2x">2.x</h4><p>\u7531 HDFS, YARN, MapReduce \u4E0E\u5176\u4ED6\u7A0B\u5E8F\u7EC4\u6210.</p><ul><li><p>\u5C06\u8D44\u6E90\u8C03\u5EA6\u5DE5\u4F5C\u5265\u79BB\u51FA, \u505A\u6210\u4E86\u72EC\u7ACB\u7684\u6846\u67B6 YARN.</p></li><li><p> \u51FA\u73B0\u4E86\u53CC NameNode \u7ED3\u6784, \u5141\u8BB8\u4E00\u4E2A StandbyNameNode (SecondaryNameNode) \u8D1F\u8D23\u70ED\u5907, \u901A\u8FC7 Quorum Journal Manager \u5B9E\u73B0\u6570\u636E\u540C\u6B65. </p></li></ul><h4 id="3x">3.x</h4><p>\u968F\u7740 2.x \u4F7F Hadoop \u80FD\u591F\u627F\u8F7D\u66F4\u5927\u7684\u96C6\u7FA4, \u800C\u6587\u4EF6\u7CFB\u7EDF\u7684\u6570\u636E\u5197\u4F59\u4E5F\u5F92\u589E, 3.x \u4E3B\u8981\u8D1F\u8D23\u6539\u5584\u53EF\u7528\u6027\u4E0E\u8D44\u6E90\u5229\u7528\u7387.</p><ul><li>HDFS \u5F15\u5165\u4E86\u7EA0\u5220\u7801\u529F\u80FD.</li><li>\u53EF\u4EE5\u6709\u66F4\u591A\u7684 StandbyNameNode \u4E86.</li><li>\u9694\u79BB Client, \u5F15\u5165 Router \u4E0E State Store \u7EC4\u6210\u7684\u62E6\u622A\u8F6C\u53D1\u5C42\u5BF9 Client \u8FDB\u884C\u4EA4\u4E92.</li></ul><h3 id="hadoop-%E9%AB%98%E5%8F%AF%E7%94%A8">Hadoop \u9AD8\u53EF\u7528</h3><blockquote><p>\u6211\u5907\u4EFD\u4E86\u5143\u6570\u636E, \u5F53\u6574\u4E2A\u96C6\u7FA4\u5D29\u6E83, \u53EA\u5269\u4E0B\u51E0\u4E2A datanode \u7684\u65F6\u5019, \u662F\u5426\u53EF\u4EE5\u6062\u590D?</p></blockquote><h3 id="hadoop-%E9%85%8D%E7%BD%AE%E8%B0%83%E4%BC%98">Hadoop \u914D\u7F6E\u8C03\u4F18</h3><ul><li>MapReduce \u8BA1\u7B97\u662F\u78C1\u76D8 I/O \u884C\u4E3A, \u6240\u4EE5\u8C03\u6574\u9884\u8BFB\u7F13\u51B2\u533A\u5927\u5C0F (core-site.xml &gt; buffer.size).</li><li>\u8C03\u6574 Block \u7684\u5927\u5C0F (hdfs-site.xml &gt; dfs.Block.size).</li></ul><h2 id="hdfs">HDFS</h2><h3 id="hdfs-%E6%96%87%E4%BB%B6%E7%9A%84%E4%B8%80%E4%BA%9B%E6%A6%82%E5%BF%B5">HDFS \u6587\u4EF6\u7684\u4E00\u4E9B\u6982\u5FF5</h3><ul><li><p> \u5143\u4FE1\u606F: \u662F\u6570\u636E\u6587\u4EF6\u7684 Block \u5927\u5C0F, \u6587\u4EF6\u526F\u672C\u5B58\u50A8\u4F4D\u7F6E, \u526F\u672C\u6570\u91CF, Block \u6570\u91CF, \u4E3B\u8981\u4F53\u73B0\u5728 Edits \u6587\u4EF6\u548C Fsimage \u6587\u4EF6. </p></li><li><p>\u526F\u672C\u6570: HDFS \u4E2D\u540C\u4E00\u4E2A\u6587\u4EF6\u5728\u591A\u4E2A\u8282\u70B9\u4E2D\u6240\u5B58\u50A8\u7684\u603B\u6570\u91CF, \u4E5F\u662F\u5B9E\u73B0\u6301\u4E45\u5316\u548C\u4FDD\u8BC1\u5B89\u5168\u6027\u7684\u5173\u952E.</p></li><li><p>\u6587\u4EF6\u76EE\u5F55\u6811: HDFS \u63D0\u4F9B\u4E86\u4E00\u4E2A\u53EF\u4EE5\u7EF4\u62A4\u7684\u6587\u4EF6\u76EE\u5F55, \u8BE5\u6587\u4EF6\u76EE\u5F55\u4E0B\u5B58\u50A8\u7740\u6709\u5173\u6240\u6709 HDFS \u7684\u6587\u4EF6.</p></li><li><p>Block \u6570\u636E\u8282\u70B9\u4FE1\u606F: \u5982 a \u6587\u4EF6\u5728 01 \u548C 02 \u8282\u70B9\u4E2D\u5B58\u50A8, \u8BE5\u4FE1\u606F\u79F0\u4E3A\u6570\u636E\u8282\u70B9\u4FE1\u606F.</p></li><li><p>Edits: \u8BB0\u5F55 Client \u6267\u884C\u521B\u5EFA,\u79FB\u52A8,\u4FEE\u6539\u6587\u4EF6\u7684\u4FE1\u606F, \u540C\u65F6\u4F53\u73B0\u4E86 HDFS \u7684\u6700\u65B0\u7684\u72B6\u6001 (\u4E8C\u8FDB\u5236\u6587\u4EF6).</p><ul><li> \u5B83\u5206\u5E03\u5728\u78C1\u76D8\u4E0A\u7684\u591A\u4E2A\u6587\u4EF6, \u540D\u79F0\u7531\u524D\u7F00 Edits \u53CA\u540E\u7F00\u7EC4\u6210.\u540E\u7F00\u503C\u662F\u8BE5\u6587\u4EF6\u5305\u542B\u7684\u4E8B\u52A1 ID,\u540C\u4E00\u65F6\u523B\u53EA\u6709\u4E00\u4E2A\u6587\u4EF6\u5904\u4E8E\u53EF\u8BFB\u5199\u72B6\u6001.\u4E3A\u907F\u514D\u6570\u636E\u4E22\u5931,\u4E8B\u52A1\u5B8C\u6210\u540E client \u7AEF\u5728\u6267\u884C\u6210\u529F\u524D,\u6587\u4EF6\u4F1A\u8FDB\u884C\u66F4\u65B0\u548C\u540C\u6B65,\u5F53 NN \u5411\u591A\u4E2A\u76EE\u5F55\u5199\u6570\u636E\u65F6,\u53EA\u6709\u5728\u6240\u6709\u64CD\u4F5C\u66F4\u65B0\u5E76\u540C\u6B65\u5230\u6BCF\u4E2A\u526F\u672C\u4E4B\u540E\u6267\u884C\u624D\u6210\u529F. </li></ul></li><li><p>Fsimage: \u8BB0\u5F55\u7684\u662F\u6570\u636E\u5757\u7684\u4F4D\u7F6E\u4FE1\u606F, \u6570\u636E\u5757\u7684\u5197\u4F59\u4FE1\u606F (\u4E8C\u8FDB\u5236\u6587\u4EF6).</p><ul><li> \u7531\u4E8E Edits \u6587\u4EF6\u8BB0\u5F55\u4E86\u6700\u65B0\u72B6\u6001\u4FE1\u606F, \u5E76\u4E14\u968F\u7740\u64CD\u4F5C\u8D8A\u591A, Edits \u6587\u4EF6\u5C31\u4F1A\u8D8A\u5927, \u628A Edits \u6587\u4EF6\u4E2D\u6700\u65B0\u7684\u4FE1\u606F\u5199\u5230 fsimage \u6587\u4EF6\u4E2D\u5C31\u89E3\u51B3\u4E86 Edits \u6587\u4EF6\u6570\u91CF\u591A\u4E0D\u65B9\u4FBF\u7BA1\u7406\u7684\u60C5\u51B5. \u6CA1\u6709\u4F53\u73B0 HDFS \u7684\u6700\u65B0\u72B6\u6001. </li></ul></li><li><p>\u6BCF\u4E2A fsimage \u6587\u4EF6\u90FD\u662F\u6587\u4EF6\u7CFB\u7EDF\u5143\u6570\u636E\u7684\u4E00\u4E2A\u5B8C\u6574\u7684\u6C38\u4E45\u6027\u7684\u68C0\u67E5\u70B9.</p></li></ul><h3 id="hdfs-%E7%9A%84-block">HDFS \u7684 Block</h3><p>\u9ED8\u8BA4\u4FDD\u5B58 3 \u5206, \u6BCF\u4EFD 128 mb.</p><blockquote><p>1.x \u4F7F\u7528 64 mb \u4F5C\u4E3A Block \u5927\u5C0F.</p></blockquote><h4 id="block-%E5%92%8C%E8%BE%93%E5%85%A5%E5%88%86%E5%89%B2%E7%9B%B4%E6%8E%A5%E6%9C%89%E4%BB%80%E4%B9%88%E5%8C%BA%E5%88%AB"> Block \u548C\u8F93\u5165\u5206\u5272\u76F4\u63A5\u6709\u4EC0\u4E48\u533A\u522B </h4><p>HDFS \u5C06\u8F93\u5165\u6570\u636E\u7269\u7406\u4E0A\u5212\u5206\u4E3A Block; \u8F93\u5165\u62C6\u5206\u662F\u6620\u5C04\u5668\u5BF9\u6570\u636E\u7684\u903B\u8F91\u5212\u5206, \u7528\u4E8E\u6620\u5C04\u64CD\u4F5C.</p><h4 id="hdfs-%E7%9A%84-block-%E6%80%8E%E4%B9%88%E8%B0%83%E6%95%B4%E5%A4%A7%E5%B0%8F">HDFS \u7684 Block \u600E\u4E48\u8C03\u6574\u5927\u5C0F</h4><p>\u5728\u914D\u7F6E\u6587\u4EF6 hdfs-site.xml \u4E2D\u52A0\u5165, \u6240\u6709\u7684 DataNode \u90FD\u8981\u52A0.</p><pre class="hljs"><code><div>
<span class="hljs-tag">&lt;<span class="hljs-name">property</span>&gt;</span>
    <span class="hljs-tag">&lt;<span class="hljs-name">name</span>&gt;</span>dfs.Block.size<span class="hljs-tag">&lt;/<span class="hljs-name">name</span>&gt;</span>
    <span class="hljs-tag">&lt;<span class="hljs-name">value</span>&gt;</span>size=mb*1024*1024<span class="hljs-tag">&lt;/<span class="hljs-name">value</span>&gt;</span>
    <span class="hljs-tag">&lt;<span class="hljs-name">description</span>&gt;</span>\u8C03\u6574\u5927\u5C0F<span class="hljs-tag">&lt;/<span class="hljs-name">description</span>&gt;</span>
<span class="hljs-tag">&lt;/<span class="hljs-name">property</span>&gt;</span>
</div></code></pre><blockquote><p>\u5BF9\u73B0\u6709\u7684 Block \u4E0D\u8D77\u4F5C\u7528, \u82E5\u8981\u6539\u52A8\u53EF\u4EE5\u7528 DistCp (distributed copy) \u5DE5\u5177.</p></blockquote><h3 id="hdfs-%E5%AF%B9%E5%8D%95%E7%8B%AC%E4%B8%80%E4%B8%AA%E6%96%87%E4%BB%B6%E8%B0%83%E6%95%B4-block-%E5%A4%A7%E5%B0%8F"> HDFS \u5BF9\u5355\u72EC\u4E00\u4E2A\u6587\u4EF6\u8C03\u6574 Block \u5927\u5C0F </h3><pre class="hljs"><code><div>hdfs dfs -Ddfs.Blocksize=size=mb*1024*1024 -put FILE_DIR HDFS_DIR
</div></code></pre><h3 id="block-%E5%89%AF%E6%9C%AC%E6%94%BE%E7%BD%AE%E7%AD%96%E7%95%A5">Block \u526F\u672C\u653E\u7F6E\u7B56\u7565</h3><h4 id="1x-%E7%89%88%E6%9C%AC%E7%9A%84-hdfs">1.x \u7248\u672C\u7684 HDFS</h4><ol><li>\u526F\u672C 1 \u653E\u7F6E\u5728\u4E0A\u4F20\u6587\u4EF6\u7684 DataNode \u4E2D; \u96C6\u7FA4\u5916\u63D0\u4EA4\u5219\u6311\u9009\u4E00\u53F0\u78C1\u76D8\u548C CPU \u5360\u7528\u7387\u4F4E\u7684\u8282\u70B9.</li><li>\u526F\u672C 2 \u653E\u7F6E\u5728\u4E0E\u526F\u672C 1 \u4E0D\u540C\u673A\u67B6\u7684\u96C6\u7FA4\u4E0A.</li><li>\u526F\u672C 3 \u548C\u526F\u672C 1 \u653E\u540C\u4E00\u673A\u67B6\u4E0A.</li><li>\u66F4\u591A\u7684\u526F\u672C\u968F\u673A\u653E\u7F6E.</li></ol><h4 id="2x-%E7%89%88%E6%9C%AC%E7%9A%84-hdfs">2.x \u7248\u672C\u7684 HDFS</h4><ol><li>\u526F\u672C 1 \u4E0E 1.x \u7248\u672C\u7C7B\u4F3C.</li><li>\u540C\u4E0A.</li><li>\u653E\u5728\u526F\u672C 2 \u6240\u5728\u7684\u673A\u67B6\u4E0A.</li><li>\u540C\u4E0A.</li></ol><h3 id="hdfs-%E5%86%99%E8%BF%87%E7%A8%8B">HDFS \u5199\u8FC7\u7A0B</h3><ol><li>Client \u5BF9 NameNode \u53D1\u8D77\u4E0A\u4F20\u8BF7\u6C42, NameNode \u68C0\u67E5\u6587\u4EF6\u548C\u76EE\u5F55\u662F\u5426\u5B58\u5728, \u8FD4\u56DE\u662F\u5426\u53EF\u4EE5\u4E0A\u4F20.</li><li>NameNode \u67E5\u8BE2\u4ECE\u8282\u70B9\u540E\u8FD4\u56DE Client \u8BF7\u6C42\u7684\u7B2C\u4E00\u4E2A Block \u7684\u76EE\u6807 DataNode.</li><li> Client \u82E5\u8BF7\u6C42 NameNode \u4F7F\u7528\u5C31\u8FD1\u539F\u5219, \u5219\u5411\u6700\u8FD1\u7684 DataNode \u4E0A\u4F20\u6570\u636E, DataNode \u4E0E\u5176\u4ED6\u8282\u70B9\u5EFA\u7ACB Pipeline \u5E76\u9010\u7EA7\u8C03\u7528. </li><li>Client \u4E0A\u4F20\u7B2C\u4E00\u4E2A Block \u5230 DataNode, DataNode \u4EE5 Package \u4E3A\u5355\u4F4D\u5411\u5176\u4ED6\u8282\u70B9\u4F20\u8F93 Package \u5E76\u521B\u5EFA\u5E94\u7B54\u961F\u5217\u4E0E\u7B49\u5F85.</li><li> \u5F53\u7B2C\u4E00\u4E2A Block \u4F20\u8F93\u5B8C\u6210\u540E, Client \u518D\u6B21\u8BF7\u6C42 NameNode \u4E0A\u4F20\u7B2C\u4E8C\u4E2A Block. \u6B64\u65F6\u7684 Client \u4F20\u8F93\u548C Block \u7684\u6C47\u62A5\u662F\u5E76\u884C\u7684. </li></ol><h3 id="hdfs-%E8%AF%BB%E8%BF%87%E7%A8%8B">HDFS \u8BFB\u8FC7\u7A0B</h3><ol><li>Client \u521B\u5EFA\u4E00\u4E2A\u5BF9\u8C61\u4E0E NameNode \u8FDB\u884C RPC \u901A\u4FE1, \u6536\u5230 NameNode \u5BF9\u8C61\u540E, \u8BF7\u6C42\u83B7\u53D6\u6587\u4EF6\u7684\u5143\u6570\u636E.</li><li>NameNode \u6821\u9A8C\u540E\u8FD4\u56DE\u5143\u6570\u636E.</li><li>Client \u62FF\u5230\u5143\u6570\u636E\u540E\u8BFB\u53D6 DataNode \u4E2D\u7684 Block, \u5E76\u5408\u5E76 Block \u6210\u5355\u6587\u4EF6\u7136\u540E\u8FD4\u56DE.</li></ol><h3 id="%E5%BE%80-hdfs-%E9%87%8C-put-%E6%96%87%E4%BB%B6%E6%97%B6-hdfs-%E9%83%BD%E5%81%9A%E4%BA%86%E4%BB%80%E4%B9%88"> \u5F80 HDFS \u91CC put \u6587\u4EF6\u65F6 HDFS \u90FD\u505A\u4E86\u4EC0\u4E48 </h3><h2 id="mapreduce">MapReduce</h2><h3 id="mapreduce-%E7%9A%84%E5%B7%A5%E4%BD%9C%E5%8E%9F%E7%90%86%E4%B8%8E%E6%B5%81%E7%A8%8B"> MapReduce \u7684\u5DE5\u4F5C\u539F\u7406\u4E0E\u6D41\u7A0B </h3><p>\u539F\u7406:</p><ol><li><p> MapReduce \u5C06\u5F97\u5230\u7684 Split \u5206\u914D\u5BF9\u5E94\u7684 Task, \u6BCF\u4E2A\u4EFB\u52A1\u5904\u7406\u76F8\u5BF9\u5E94\u7684 Split, \u4EE5 Line \u65B9\u5F0F\u8BFB\u53D6\u5355\u884C\u6570\u636E, \u6570\u636E\u4F9D\u6B21\u8BFB\u5230 <code>mapreduce.Task.io.sort.mb</code> \u7684\u73AF\u5F62\u7F13\u51B2\u533A. </p></li><li><p> \u8BFB\u53D6\u8FC7\u7A0B\u4E2D\u4E00\u65E6\u8FBE\u5230\u9608\u503C <code>mapreduce.map.sort.spill.percent</code> \u5219\u8FDB\u884C\u6EA2\u5199\u64CD\u4F5C, Spiller \u7EBF\u7A0B\u6EA2\u5199\u5230\u78C1\u76D8 <code>mapreduce.cluster.local.dir</code> \u76EE\u5F55\u4E2D, \u671F\u95F4\u8FDB\u884C K/V \u5206\u533A, \u5206\u533A\u6570\u7531 Reduce \u6570\u51B3\u5B9A, \u9ED8\u8BA4\u4F7F\u7528 HashPartitioner. </p></li><li><p> \u518D\u5C06\u5206\u533A\u4E2D\u6570\u636E\u6309\u7167 Key \u5206\u7EC4\u548C\u6392\u5E8F, \u9ED8\u8BA4\u662F\u5B57\u5178\u548C\u5347\u5E8F. \u5982\u679C\u8BBE\u7F6E\u4E86 setCombinerClass \u5219\u4F1A\u5BF9\u6BCF\u4E2A\u5206\u533A\u4E2D\u7684\u6570\u636E\u8FDB\u884C Combiner \u64CD\u4F5C. <code>output.compress</code> \u8FD8\u4F1A\u538B\u7F29\u6EA2\u5199\u7684\u6570\u636E. </p></li><li><p>\u4E4B\u540E Merge \u6839\u636E\u5206\u533A\u89C4\u5219, \u5C06\u6570\u636E\u5F52\u5E76\u5230\u4E00\u4E2A\u6587\u4EF6\u91CC\u7B49\u5F85 Reduce PULL \u5230\u4E00\u4E2A\u8282\u70B9\u4E0A, .</p></li><li><p> NodeManager \u5C06\u542F\u52A8\u4E00\u4E2A <code>mapreduce_Shuffle</code> \u670D\u52A1\u5C06\u6570\u636E\u4EE5 HTTP \u7684\u65B9\u5F0F PULL \u5230\u4E00\u4E2A\u8282\u70B9\u4E0A, \u5230 Reduce \u4E0A. Reduce \u5904\u7406\u8FBE\u5230\u9608\u503C\u6216 Map \u8F93\u51FA\u8FBE\u5230\u9608\u503C\u4FBF Merge ( \u540C\u4E00\u5206\u533A\u7684\u4E00\u7EC4\u6570\u636E\u4F1A\u5148\u8FDB\u884C\u5F52\u5E76), Sort (\u5C06\u5F52\u5E76\u597D\u7684\u6570\u636E\u8FDB\u884C\u6392\u5E8F), group (\u5224\u65AD\u8FED\u4EE3\u5668\u4E2D\u7684\u5143\u7D20\u662F\u5426\u53EF\u4EE5\u8FED\u4EE3), \u5904\u7406\u5B8C\u6210\u540E MapReduce \u5C06\u540C\u4E00\u5206\u533A\u5185\u7684\u6570\u636E\u5199\u5165 HDFS. </p></li></ol><blockquote><p> \u5176\u4E2D Reduce \u7684 Merge \u8FBE\u5230\u9608\u503C\u4F1A\u89E6\u53D1, Sort \u5219\u662F\u7EF4\u6301 Map \u9636\u6BB5\u7684\u6392\u5E8F, Group \u8BBE\u7F6E <code>setGroupingComparatorClass</code> \u540E\u624D\u4F1A\u89E6\u53D1. </p></blockquote><p>\u6D41\u7A0B:</p><ol><li>Client \u542F\u52A8\u4E00\u4E2A Job, \u5411 JobTracker \u8BF7\u6C42\u4E00\u4E2A JobID.</li><li> Client \u5C06\u6240\u9700\u6570\u636E\u4E0A\u4F20\u7ED9 HDFS, \u5305\u62EC MapReduce \u6253\u5305\u7684 jar \u6587\u4EF6, \u914D\u7F6E\u6587\u4EF6, \u4EE5\u53CA\u8BA1\u7B97\u6240\u9700\u7684\u8F93\u5165\u5212\u5206\u4FE1\u606F; \u8FD9\u4E9B\u6587\u4EF6\u50A8\u5B58\u5728 JobTracker \u7684 JobID \u76EE\u5F55\u4E2D, jar \u4F1A\u521B\u5EFA\u591A\u4E2A\u526F\u672C, \u8F93\u5165\u5212\u5206\u4FE1\u606F\u5BF9\u5E94\u7740 JobTracker \u5E94\u542F\u52A8\u591A\u5C11\u4E2A Map \u4EFB\u52A1. </li><li>JobTracker \u5C06\u8D44\u6E90\u653E\u5165\u4F5C\u4E1A\u961F\u5217\u4E2D, \u8C03\u5EA6\u5668\u8C03\u5EA6\u540E\u6839\u636E\u8F93\u5165\u5212\u5206\u4FE1\u606F\u5212\u5206 Map \u4EFB\u52A1\u5E76\u5206\u53D1\u7ED9 TaskTracker \u6267\u884C.</li><li>TaskTracker \u5FC3\u8DF3\u8BBF\u95EE JobTracker, \u8BBF\u95EE\u5185\u5BB9\u5305\u542B Map \u4EFB\u52A1\u8FDB\u5EA6.</li><li>\u6700\u540E\u4E00\u4E2A\u4EFB\u52A1\u5B8C\u6210\u540E, JobTracker \u8BBE\u7F6E\u8FD9\u4E2A\u4EFB\u52A1\u4E3A\u6210\u529F, \u5E76\u8FD4\u56DE\u7ED9 Client, Client \u518D\u901A\u77E5\u7ED9\u64CD\u4F5C\u8005.</li></ol><h3 id="map-%E7%9A%84%E8%BF%90%E8%A1%8C%E6%AD%A5%E9%AA%A4">Map \u7684\u8FD0\u884C\u6B65\u9AA4</h3><ol><li>Mapper \u6839\u636E\u6587\u4EF6\u5206\u533A.</li><li>Sort \u5C06 Mapper \u4EA7\u751F\u7684\u7ED3\u679C\u6309\u7167 Key \u8FDB\u884C\u6392\u5217.</li><li>Combiner \u5C06 Key \u76F8\u540C\u7684\u8BB0\u5F55\u5408\u5E76.</li><li>Partition \u628A\u6570\u636E\u5747\u8861\u7684\u5206\u53D1\u7ED9 Reducer.</li><li>Shuffle \u5C06 Mapper \u7684\u7ED3\u679C\u4F20\u8F93\u7ED9 Reduce, \u4E5F\u662F\u6570\u636E\u503E\u659C\u4F1A\u51FA\u73B0\u7684\u6B65\u9AA4.</li></ol><h3 id="mapreduce-%E7%9A%84%E6%95%B0%E6%8D%AE%E5%80%BE%E6%96%9C">MapReduce \u7684\u6570\u636E\u503E\u659C</h3><blockquote><p> \u6570\u636E\u503E\u659C\u53D1\u751F\u5728 Reduce \u7AEF. Mapper \u5904\u7406\u5B8C\u6570\u636E\u4F20\u7ED9 Reduce, \u6B64\u65F6 Reduce \u4F1A\u56E0\u4E3A\u5927\u91CF\u7684 Key \u5BFC\u81F4\u6267\u884C\u65F6\u95F4\u8FC7\u957F\u5F15\u8D77\u5835\u585E. </p></blockquote><p>\u4F18\u5316\u6570\u636E\u503E\u659C:</p><p> \u5BF9\u6570\u636E\u8FDB\u884C\u6E05\u6D17\u4E0E\u6CBB\u7406. \u53EF\u4EE5\u5728 Mapper \u671F\u95F4\u5C06\u5927\u91CF\u76F8\u540C\u7684 Key \u6253\u6563, \u6BD4\u5982\u6DFB\u52A0 N \u4EE5\u5185\u7684\u968F\u673A\u6570\u524D\u7F00; \u53EF\u4EE5\u5BF9\u6570\u636E\u8F83\u591A\u7684 Key \u8FDB\u884C\u5B50\u6269\u5C55, \u5148\u8FDB\u884C\u5C40\u90E8\u64CD\u4F5C, \u518D\u53BB\u9664\u968F\u673A\u6570\u540E Combiner, \u907F\u514D\u5728 Shuffle \u65F6\u51FA\u73B0\u6570\u636E\u503E\u659C. </p><h3 id="mapper-combiner-%E5%90%8E%E4%BC%9A%E5%8F%91%E7%94%9F%E4%BB%80%E4%B9%88">Mapper Combiner \u540E\u4F1A\u53D1\u751F\u4EC0\u4E48</h3><p>\u8FD0\u884C\u901F\u5EA6\u4F1A\u63D0\u5347, Mapper \u5230 Reduce \u7684\u6570\u636E\u91CF\u4E5F\u4F1A\u53D8\u5C11, \u56E0\u4E3A Combiner \u628A\u76F8\u540C\u7684 Key \u5408\u5E76\u4E86.</p><h3 id="map-%E8%BE%93%E5%87%BA%E7%9A%84%E6%95%B0%E6%8D%AE%E8%B6%85%E5%87%BA%E5%B0%8F%E6%96%87%E4%BB%B6%E5%86%85%E5%AD%98%E5%90%8E%E4%BC%9A%E5%8F%91%E7%94%9F%E4%BB%80%E4%B9%88"> Map \u8F93\u51FA\u7684\u6570\u636E\u8D85\u51FA\u5C0F\u6587\u4EF6\u5185\u5B58\u540E\u4F1A\u53D1\u751F\u4EC0\u4E48 </h3><p>\u6570\u636E\u4F1A\u5199\u5165\u5230\u78C1\u76D8\u4E2D. Map, Reduce \u662F I/O \u64CD\u4F5C.</p><h3 id="map-%E5%88%B0-reduce-%E9%BB%98%E8%AE%A4%E7%9A%84%E5%88%86%E5%8C%BA%E6%9C%BA%E5%88%B6"> Map \u5230 Reduce \u9ED8\u8BA4\u7684\u5206\u533A\u673A\u5236 </h3><p>\u5BF9 Map \u4E2D\u7684 Key \u53D6\u54C8\u5E0C\u503C, \u5BF9 Reduce \u7684\u4E2A\u6570\u53D6\u6A21.</p><h3 id="split-%E6%9C%BA%E5%88%B6">Split \u673A\u5236</h3><p> Spilt \u662F MapReduce \u4E2D Map \u4E4B\u524D\u7684\u6982\u5FF5. Split \u5207\u7247\u5927\u5C0F\u9ED8\u8BA4\u4E3A Block \u7684 1.1 \u500D, \u5728 <code>FileInputFormat</code> \u4E2D\u8BA1\u7B97\u5207\u7247\u5927\u5C0F\u7684\u903B\u8F91: </p><pre class="hljs"><code><div><span class="hljs-keyword">public</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">final</span> String SPLIT_MAXSIZE=<span class="hljs-string">&quot;mapreduce.input.fileinputformat.split.maxsize&quot;</span>;
<span class="hljs-keyword">public</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">final</span> String SPLIT_MINSIZE=<span class="hljs-string">&quot;mapreduce.input.fileinputformat.split.minsize&quot;</span>;
<span class="hljs-function"><span class="hljs-keyword">protected</span> <span class="hljs-keyword">long</span> <span class="hljs-title">computeSplitSize</span><span class="hljs-params">(<span class="hljs-keyword">long</span> BlockSize,<span class="hljs-keyword">long</span> minSize,<span class="hljs-keyword">long</span> maxSize)</span></span>{
        <span class="hljs-keyword">return</span> Math.max(minSize,Math.min(maxSize,BlockSize));
        }
</div></code></pre><h3 id="%E4%B8%BA%E4%BB%80%E4%B9%88-split-%E4%B8%8D%E4%B8%8E-block-%E5%AF%B9%E5%BA%94"> \u4E3A\u4EC0\u4E48 Split \u4E0D\u4E0E Block \u5BF9\u5E94 </h3><p>\u5927\u91CF\u5C0F\u6587\u4EF6\u573A\u666F\u4E0B Map \u8FDB\u7A0B\u9020\u6210\u7684\u8D44\u6E90\u4E25\u91CD\u6D6A\u8D39.</p><h3 id="shuffle-%E5%8E%9F%E7%90%86%E4%B8%8E%E4%BC%98%E5%8C%96">Shuffle \u539F\u7406\u4E0E\u4F18\u5316</h3><h2 id="yarn">YARN</h2><h3 id="yarn-%E7%9A%84%E4%BC%98%E5%8A%BF">YARN \u7684\u4F18\u52BF</h3><p> YARN \u96C6\u7FA4\u4EE5\u4E3B\u4ECE\u67B6\u6784\u7EC4\u7EC7, \u4E3B\u8282\u70B9 ReSourceManage \u8D1F\u8D23\u8D44\u6E90\u8C03\u5EA6\u5206\u914D, NodeMange \u8D1F\u8D23\u8BA1\u7B97\u8282\u70B9\u7BA1\u7406, \u8D44\u6E90\u76D1\u63A7\u548C\u542F\u52A8\u5E94\u7528\u6240\u9700\u7684 Combiner. YARN \u4E00\u822C\u548C MapReduce \u7ED3\u5408, \u4E3B\u8981\u5BF9 MapReduce \u4E2D\u7684\u8D44\u6E90\u8BA1\u7B97\u8FDB\u884C\u7EF4\u62A4. </p><h3 id="mapreduce-on-yarn-%E5%B7%A5%E4%BD%9C%E6%B5%81%E7%A8%8B">MapReduce on YARN \u5DE5\u4F5C\u6D41\u7A0B</h3><ol><li>\u5411 Client \u63D0\u4EA4 MapReduce Job.</li><li>YARN \u7684 ReSourceManager \u8FDB\u884C\u8D44\u6E90\u5206\u914D.</li><li>NodeManager \u52A0\u8F7D\u5E76\u76D1\u63A7 Containers.</li><li> \u901A\u8FC7 ApplicationMaster \u4E0E ReSourceManager \u8FDB\u884C\u8D44\u6E90\u7684\u7533\u8BF7\u548C\u72B6\u6001\u4EA4\u4E92, \u7531 NodeManagers \u8FDB\u884C MapReduce \u8FD0\u884C\u65F6 Job \u7684\u7BA1\u7406. </li><li>\u901A\u8FC7 HDFS \u8FDB\u884C Job \u914D\u7F6E\u6587\u4EF6, Jar \u5305\u7684\u8282\u70B9\u5206\u53D1.</li></ol>`,89),s=E({__name:"hadoop",setup(C){return(i,D)=>(F(),B(l,null,[a,A(u)],64))}});export{s as default};
