import{B as u}from"./BackToTopButton.4b5a936b.js";import{d as E,o as B,c as F,b as A,F as D,e as a}from"./index.f6e50d68.js";const i=a('<title>Blog Kafka</title><div class="container"><div class="content"><h1 id="%E5%88%86%E5%B8%83%E5%BC%8F%E4%B8%AD%E9%97%B4%E4%BB%B6-kafka">\u5206\u5E03\u5F0F\u4E2D\u95F4\u4EF6 Kafka</h1><p>\u76EE\u5F55</p><ol><li><a href="#%E5%88%86%E5%B8%83%E5%BC%8F%E4%B8%AD%E9%97%B4%E4%BB%B6-kafka">\u5206\u5E03\u5F0F\u4E2D\u95F4\u4EF6 Kafka</a><ol><li><a href="#kafka">Kafka</a><ol><li><a href="#%E7%9B%B8%E5%85%B3%E6%A6%82%E5%BF%B5">\u76F8\u5173\u6982\u5FF5</a><ol><li><a href="#isr-ar-hw-leo">ISR, AR, HW, LEO</a></li><li><a href="#kafka-%E6%8B%A6%E6%88%AA%E5%99%A8-%E5%BA%8F%E5%88%97%E5%8C%96%E5%99%A8-%E5%88%86%E5%8C%BA%E5%99%A8">Kafka \u62E6\u622A\u5668, \u5E8F\u5217\u5316\u5668, \u5206\u533A\u5668</a></li><li><a href="#kafka-%E6%95%B0%E6%8D%AE%E6%A0%BC%E5%BC%8F%E9%83%BD%E6%98%AF%E4%BB%80%E4%B9%88">kafka \u6570\u636E\u683C\u5F0F\u90FD\u662F\u4EC0\u4E48</a></li><li><a href="#kafka-%E7%9A%84-broker-partition-segment-%E9%83%BD%E6%98%AF%E5%95%A5">kafka \u7684 <code>Broker, partition, segment</code> \u90FD\u662F\u5565</a></li><li><a href="#kafka-%E4%B8%80%E6%9D%A1-message-%E4%B8%AD%E5%8C%85%E5%90%AB%E5%93%AA%E4%BA%9B%E4%BF%A1%E6%81%AF">kafka \u4E00\u6761 Message \u4E2D\u5305\u542B\u54EA\u4E9B\u4FE1\u606F</a></li></ol></li><li><a href="#kafka-%E6%95%B0%E6%8D%AE%E7%A7%AF%E5%8E%8B-%E6%B6%88%E8%B4%B9%E8%83%BD%E5%8A%9B%E4%B8%8D%E8%B6%B3%E6%80%8E%E4%B9%88%E5%A4%84%E7%90%86">Kafka \u6570\u636E\u79EF\u538B, \u6D88\u8D39\u80FD\u529B\u4E0D\u8DB3\u600E\u4E48\u5904\u7406</a></li><li><a href="#kafka-%E5%88%86%E5%8C%BA">Kafka \u5206\u533A</a><ol><li><a href="#%E5%88%86%E5%8C%BA%E7%9A%84%E5%A2%9E%E5%88%A0">\u5206\u533A\u7684\u589E\u5220</a></li><li><a href="#kafka-%E8%87%AA%E5%B8%A6%E7%9A%84-topic">Kafka \u81EA\u5E26\u7684 Topic</a></li><li><a href="#%E5%88%86%E5%8C%BA%E7%9A%84%E5%88%86%E9%85%8D%E7%9A%84%E6%A6%82%E5%BF%B5">\u5206\u533A\u7684\u5206\u914D\u7684\u6982\u5FF5</a></li></ol></li><li><a href="#kafka-%E6%80%8E%E4%B9%88%E8%BF%9B%E8%A1%8C%E6%95%B0%E6%8D%AE%E5%A4%87%E4%BB%BD">Kafka \u600E\u4E48\u8FDB\u884C\u6570\u636E\u5907\u4EFD</a></li><li><a href="#consumer-%E5%9C%A8-leader-%E8%BF%98%E6%98%AF-follower-%E4%B8%AD%E6%8B%BF%E5%8E%BB%E6%95%B0%E6%8D%AE">Consumer \u5728 Leader \u8FD8\u662F Follower \u4E2D\u62FF\u53BB\u6570\u636E</a></li><li><a href="#kafka-%E6%95%B0%E6%8D%AE%E4%B8%80%E8%87%B4%E6%80%A7-isr-%E6%9C%BA%E5%88%B6">Kafka \u6570\u636E\u4E00\u81F4\u6027 ISR \u673A\u5236</a></li><li><a href="#kafka-%E6%95%B0%E6%8D%AE%E4%B8%8D%E4%B8%A2%E5%A4%B1%E4%B8%8D%E9%87%8D%E5%A4%8D">Kafka \u6570\u636E\u4E0D\u4E22\u5931\u4E0D\u91CD\u590D</a><ol><li><a href="#%E4%B8%8D%E9%87%8D%E5%A4%8D%E5%8E%9F%E7%90%86">\u4E0D\u91CD\u590D\u539F\u7406</a></li><li><a href="#kafka-%E5%A6%82%E4%BD%95%E5%AE%9E%E7%8E%B0%E5%B9%82%E7%AD%89%E6%80%A7">Kafka \u5982\u4F55\u5B9E\u73B0\u5E42\u7B49\u6027</a></li></ol></li><li><a href="#kafka-%E9%87%8D%E5%A4%8D%E6%B6%88%E8%B4%B9%E4%B8%8E%E6%BC%8F%E6%B6%88%E8%B4%B9">Kafka \u91CD\u590D\u6D88\u8D39\u4E0E\u6F0F\u6D88\u8D39</a><ol><li><a href="#%E9%87%8D%E5%A4%8D%E6%B6%88%E8%B4%B9">\u91CD\u590D\u6D88\u8D39</a></li><li><a href="#%E6%BC%8F%E6%B6%88%E8%B4%B9">\u6F0F\u6D88\u8D39</a></li></ol></li><li><a href="#kafka-%E6%95%B0%E6%8D%AE%E9%A1%BA%E5%BA%8F%E4%B8%8E%E6%B6%88%E6%81%AF%E9%A1%BA%E5%BA%8F">Kafka \u6570\u636E\u987A\u5E8F\u4E0E\u6D88\u606F\u987A\u5E8F</a><ol><li><a href="#%E8%A7%A3%E5%86%B3%E6%95%B0%E6%8D%AE%E9%A1%BA%E5%BA%8F">\u89E3\u51B3\u6570\u636E\u987A\u5E8F</a></li><li><a href="#kafka-%E4%BD%93%E7%8E%B0%E6%B6%88%E6%81%AF%E6%9C%89%E5%BA%8F%E6%80%A7">Kafka \u4F53\u73B0\u6D88\u606F\u6709\u5E8F\u6027</a></li></ol></li><li><a href="#kafka-%E4%B8%BA%E4%BB%80%E4%B9%88%E9%80%9F%E5%BA%A6%E5%BF%AB">Kafka \u4E3A\u4EC0\u4E48\u901F\u5EA6\u5FEB</a></li><li><a href="#kafka-%E5%A6%82%E4%BD%95%E6%B8%85%E7%90%86%E8%BF%87%E6%9C%9F%E6%95%B0%E6%8D%AE">Kafka \u5982\u4F55\u6E05\u7406\u8FC7\u671F\u6570\u636E</a></li><li><a href="#kafka-%E8%93%84%E6%B0%B4%E6%B1%A0%E6%9C%BA%E5%88%B6">Kafka \u84C4\u6C34\u6C60\u673A\u5236</a></li></ol></li></ol></li></ol><h2 id="kafka">Kafka</h2><h3 id="%E7%9B%B8%E5%85%B3%E6%A6%82%E5%BF%B5">\u76F8\u5173\u6982\u5FF5</h3><h4 id="isr-ar-hw-leo">ISR, AR, HW, LEO</h4><ul><li>ISR: \u4E0E leader \u4FDD\u6301\u540C\u6B65\u7684 follower \u96C6\u5408\u53EB\u505A in-sync-replicas.</li><li>AR: \u53EF\u7528\u7684 replica \u96C6\u5408\u53EB\u505A available replicas.</li><li>HW: \u6D88\u606F\u7684\u6700\u5927\u5E8F\u53F7\u53EB\u505A high watermark.</li><li>LEO: \u6D88\u606F\u7684\u6700\u540E\u5E8F\u53F7\u53EB\u505A last enqueued offset.</li></ul><h4 id="kafka-%E6%8B%A6%E6%88%AA%E5%99%A8-%E5%BA%8F%E5%88%97%E5%8C%96%E5%99%A8-%E5%88%86%E5%8C%BA%E5%99%A8"> Kafka \u62E6\u622A\u5668, \u5E8F\u5217\u5316\u5668, \u5206\u533A\u5668 </h4><ul><li> \u62E6\u622A\u5668\u5206\u4E3A\u4E24\u79CD: \u751F\u4EA7\u62E6\u622A\u5668\u548C\u6D88\u8D39\u62E6\u622A\u5668. \u751F\u4EA7\u62E6\u622A\u5668\u53EF\u4EE5\u5728\u6D88\u606F\u53D1\u9001\u524D\u505A\u9884\u5904\u7406\u5DE5\u4F5C; \u652F\u6301\u914D\u7F6E\u591A\u4E2A\u62E6\u622A\u5668\u5F62\u6210\u62E6\u622A\u94FE. </li><li> \u751F\u4EA7\u8005\u9700\u8981\u5C06\u6D88\u606F\u5BF9\u8C61\u8FDB\u884C\u5E8F\u5217\u5316\u6210\u5B57\u8282\u7801\u624D\u80FD\u901A\u8FC7\u7F51\u7EDC\u4F20\u8F93\u7ED9 Kafka \u670D\u52A1\u7AEF, \u4E14\u6D88\u8D39\u8005\u9700\u8981\u53CD\u5E8F\u5217\u5316, \u5E8F\u5217\u5316\u5668\u4E5F\u8981\u4E00\u81F4. </li><li> \u5982\u679C\u6D88\u606F ProducerRecord \u4E2D\u6307\u5B9A\u4E86 partition \u5B57\u6BB5, \u90A3\u4E48\u5C31\u4E0D\u9700\u8981\u5206\u533A\u5668\u6765\u5904\u7406, \u56E0\u4E3A partition \u4EE3\u8868\u7684\u5C31\u662F\u6240\u8981\u53D1\u5F80\u7684\u5206\u533A\u53F7; \u5982\u679C\u6CA1\u6709\u6307\u5B9A, \u5219\u9700\u8981\u5206\u533A\u5668\u8FDB\u884C\u5206\u914D. </li></ul><h4 id="kafka-%E6%95%B0%E6%8D%AE%E6%A0%BC%E5%BC%8F%E9%83%BD%E6%98%AF%E4%BB%80%E4%B9%88"> kafka \u6570\u636E\u683C\u5F0F\u90FD\u662F\u4EC0\u4E48 </h4><p>Topic \u4E3B\u9898, \u7136\u540E\u4E3B\u9898\u8FDB\u884C\u5206\u533A, Topic \u5206\u4E3A Partition, Partition \u5305\u542B Message.</p><h4 id="kafka-%E7%9A%84-broker-partition-segment-%E9%83%BD%E6%98%AF%E5%95%A5"> kafka \u7684 <code>Broker, partition, segment</code> \u90FD\u662F\u5565 </h4><p> \u4E00\u4E2A Kafka \u670D\u52A1\u5668\u4E5F\u79F0\u4E3A Broker, \u5B83\u63A5\u53D7\u751F\u4EA7\u8005\u53D1\u9001\u7684\u6D88\u606F\u5E76\u5B58\u5165\u78C1\u76D8; Broker \u540C\u65F6\u670D\u52A1\u6D88\u8D39\u8005\u62C9\u53D6\u5206\u533A\u6D88\u606F\u7684\u8BF7\u6C42, \u8FD4\u56DE\u76EE\u524D\u5DF2\u7ECF\u63D0\u4EA4\u7684\u6D88\u606F. </p><p>\u6BCF\u4E00\u4E2A Partition \u6700\u7EC8\u5BF9\u5E94\u4E00\u4E2A\u76EE\u5F55, \u91CC\u9762\u5B58\u50A8\u6240\u6709\u7684\u6D88\u606F\u548C\u7D22\u5F15\u6587\u4EF6.</p><p> Segment File \u53C8\u7531 index file \u548C data file \u7EC4\u6210, \u4ED6\u4EEC\u603B\u662F\u6210\u5BF9\u51FA\u73B0, \u540E\u7F00 &quot;.index&quot; \u548C &quot;.log&quot; \u5206\u8868\u8868\u793A Segment \u7D22\u5F15\u6587\u4EF6\u548C\u6570\u636E\u6587\u4EF6. </p><h4 id="kafka-%E4%B8%80%E6%9D%A1-message-%E4%B8%AD%E5%8C%85%E5%90%AB%E5%93%AA%E4%BA%9B%E4%BF%A1%E6%81%AF"> kafka \u4E00\u6761 Message \u4E2D\u5305\u542B\u54EA\u4E9B\u4FE1\u606F </h4><p>\u5305\u542B Header \u548C Body.</p><p>\u4E00\u4E2A Kafka Message \u7531\u4E00\u4E2A\u56FA\u5B9A\u957F\u5EA6\u7684\u6D88\u606F\u5934 Header \u548C\u4E00\u4E2A\u53D8\u957F Body \u7684\u6D88\u606F\u4F53\u7EC4\u6210.</p><p>Header \u7531\u4E00\u4E2A\u5B57\u8282\u7684 magic (\u6587\u4EF6\u683C\u5F0F\u4FE1\u606F) \u548C\u56DB\u4E2A\u5B57\u8282\u7684 CRC32 \u6821\u9A8C\u7801 (\u5224\u65AD Body \u662F\u5426\u6B63\u5E38) \u7EC4\u6210.</p><p>\u5F53 magic \u4E3A 1 \u65F6, \u4F1A\u5728\u4E24\u8005\u4E2D\u6DFB\u52A0\u4E00\u4E2A\u5B57\u8282\u7684\u6570\u636E attributes, \u7528\u4E8E\u4FDD\u5B58\u6587\u4EF6\u76F8\u5173\u7684\u4FE1\u606F, \u6BD4\u5982\u662F\u5426\u538B\u7F29.</p><p>magic \u4E3A 0 \u65F6, \u5219 Body \u76F4\u63A5\u7531 N \u4E2A\u5B57\u8282\u6784\u6210\u4E00\u4E2A\u5177\u4F53\u952E\u503C\u5BF9\u5F62\u5F0F\u7684\u6D88\u606F.</p><h3 id="kafka-%E6%95%B0%E6%8D%AE%E7%A7%AF%E5%8E%8B-%E6%B6%88%E8%B4%B9%E8%83%BD%E5%8A%9B%E4%B8%8D%E8%B6%B3%E6%80%8E%E4%B9%88%E5%A4%84%E7%90%86"> Kafka \u6570\u636E\u79EF\u538B, \u6D88\u8D39\u80FD\u529B\u4E0D\u8DB3\u600E\u4E48\u5904\u7406 </h3><ul><li>Kafka \u53EF\u4EE5\u540C\u6B65\u589E\u52A0 Topic \u7684\u5206\u533A\u91CF\u548C\u6D88\u8D39\u8005\u6570\u91CF, \u6D88\u8D39\u8005\u6570 = \u5206\u533A\u6570.</li><li>\u4E0B\u6E38\u5E94\u7528\u6D88\u8D39\u80FD\u529B\u4E0D\u8DB3\u65F6, \u53EF\u4EE5\u63D0\u9AD8\u6BCF\u6279\u6B21 PULL \u7684\u6570\u91CF, \u4F9B\u4E0D\u5E94\u6C42\u7B56\u7565.</li></ul><h3 id="kafka-%E5%88%86%E5%8C%BA">Kafka \u5206\u533A</h3><h4 id="%E5%88%86%E5%8C%BA%E7%9A%84%E5%A2%9E%E5%88%A0">\u5206\u533A\u7684\u589E\u5220</h4><ul><li>\u589E\u52A0: $KAFKA_HOME/bin/kafka-topics.sh --etc --alter --topic topic-config --partitions \u6570\u91CF</li><li>\u51CF\u5C11: \u6728\u91CC desu; \u88AB\u5220\u9664\u7684\u5206\u533A\u6570\u636E\u96BE\u4EE5\u5904\u7406.</li></ul><h4 id="kafka-%E8%87%AA%E5%B8%A6%E7%9A%84-topic">Kafka \u81EA\u5E26\u7684 Topic</h4><p>__consumer_offsets, \u7528\u6765\u4FDD\u62A4\u6D88\u8D39\u8005\u7684 offset.</p><h4 id="%E5%88%86%E5%8C%BA%E7%9A%84%E5%88%86%E9%85%8D%E7%9A%84%E6%A6%82%E5%BF%B5">\u5206\u533A\u7684\u5206\u914D\u7684\u6982\u5FF5</h4><p>\u4E00\u4E2A topic \u591A\u4E2A\u5206\u533A, \u4E00\u4E2A\u6D88\u8D39\u8005\u7EC4\u542B\u6709\u591A\u4E2A\u6D88\u8D39\u8005, \u6545\u9700\u8981\u5C06\u5206\u533A\u5206\u914D\u4E2A\u6D88\u8D39\u8005 (round robin, range).</p><h3 id="kafka-%E6%80%8E%E4%B9%88%E8%BF%9B%E8%A1%8C%E6%95%B0%E6%8D%AE%E5%A4%87%E4%BB%BD"> Kafka \u600E\u4E48\u8FDB\u884C\u6570\u636E\u5907\u4EFD </h3><p>\u5907\u4EFD\u662F 0.8 \u7248\u672C\u540E\u7684\u529F\u80FD.</p><p>\u4E00\u4E2A\u5907\u4EFD\u6570\u91CF\u4E3A n \u7684\u96C6\u7FA4\u5141\u8BB8 n-1 \u4E2A\u8282\u70B9\u5931\u6548.</p><p>\u6709\u4E00\u4E2A\u8282\u70B9\u4F5C\u4E3A Leader \u8282\u70B9, \u8D1F\u8D23\u4FDD\u5B58\u5176\u4ED6\u5907\u4EFD\u8282\u70B9\u7684\u5217\u8868, \u5E76\u7EF4\u6301\u5907\u4EFD\u95F4\u72B6\u6001\u540C\u6B65.</p><h3 id="consumer-%E5%9C%A8-leader-%E8%BF%98%E6%98%AF-follower-%E4%B8%AD%E6%8B%BF%E5%8E%BB%E6%95%B0%E6%8D%AE"> Consumer \u5728 Leader \u8FD8\u662F Follower \u4E2D\u62FF\u53BB\u6570\u636E </h3><p>\u8BFB\u5199\u64CD\u4F5C\u90FD\u6765\u81EA Leader, Follower \u53EA\u8D1F\u8D23\u6570\u636E\u5907\u4EFD\u548C\u5FC3\u8DF3\u68C0\u6D4B Leader \u5B58\u6D3B\u72B6\u6001, \u5E76\u53CA\u65F6\u9876\u66FF.</p><h3 id="kafka-%E6%95%B0%E6%8D%AE%E4%B8%80%E8%87%B4%E6%80%A7-isr-%E6%9C%BA%E5%88%B6">Kafka \u6570\u636E\u4E00\u81F4\u6027 ISR \u673A\u5236</h3><p>\u6570\u636E\u53EF\u9760\u4E3B\u8981\u662F\u4F9D\u8D56\u4E8E Broker \u4E2D\u7684 in-sync Replica \u526F\u672C\u540C\u6B65\u961F\u5217\u673A\u5236, \u4E3B\u8981\u903B\u8F91\u662F\u5236\u9020\u5197\u4F59, \u6570\u636E\u4E92\u5907.</p><ol><li>Leader \u4F1A\u7EF4\u62A4\u4E00\u4E2A\u4E0E\u5176\u4FDD\u6301\u540C\u6B65\u7684 Replica \u96C6\u5408, \u7136\u540E\u4FDD\u8BC1\u8FD9\u7EC4\u96C6\u5408\u81F3\u5C11\u6709\u4E00\u4E2A\u5B58\u6D3B, \u5E76\u4E14\u6D88\u606F Commit \u6210\u529F.</li><li> Partition Leader \u4FDD\u6301\u540C\u6B65\u7684 Partition Follower \u96C6\u5408, \u5F53 ISR \u4E2D\u7684 Partition Follower \u5B8C\u6210\u6570\u636E\u7684\u540C\u6B65\u4E4B\u540E\u7ED9 Leader \u53D1\u9001 ack. </li><li> \u5982\u679C Partition Follower \u957F\u65F6\u95F4 (Replica.lag.time.max.ms) \u672A\u5411 Leader \u540C\u6B65\u6570\u636E, \u5219\u8BE5 Partition Follower \u5C06\u88AB\u8E22\u51FA ISR. </li><li>Partition Leader \u53D1\u751F\u6545\u969C\u4E4B\u540E, \u5C31\u4F1A\u4ECE ISR \u4E2D\u9009\u4E3E\u65B0\u7684 Partition Leader.</li><li>\u5F53 ISR \u4E2D\u6240\u6709 Replica \u90FD\u5411 Leader \u53D1\u9001 ACK \u65F6, Leader \u6267\u884C Commit.</li></ol><h3 id="kafka-%E6%95%B0%E6%8D%AE%E4%B8%8D%E4%B8%A2%E5%A4%B1%E4%B8%8D%E9%87%8D%E5%A4%8D"> Kafka \u6570\u636E\u4E0D\u4E22\u5931\u4E0D\u91CD\u590D </h3><p>tldr:</p><ul><li>Consumer: \u4E1A\u52A1\u903B\u8F91\u6210\u529F\u5904\u7406\u540E\u624B\u52A8\u63D0\u4EA4 offset.</li><li>\u4FDD\u8BC1\u4E0D\u91CD\u590D\u6D88\u8D39: \u4F7F\u7528\u4E3B\u952E\u6216\u552F\u4E00\u7D22\u5F15\u7684\u65B9\u5F0F\u907F\u514D\u91CD\u590D\u6570\u636E.</li><li>\u4E1A\u52A1\u903B\u8F91\u5904\u7406: \u9009\u62E9\u4E3B\u952E\u6216\u552F\u4E00\u7D22\u5F15\u5B58\u50A8\u5728 K/V \u6570\u636E\u5E93\u91CC.</li></ul><h4 id="%E4%B8%8D%E9%87%8D%E5%A4%8D%E5%8E%9F%E7%90%86">\u4E0D\u91CD\u590D\u539F\u7406</h4><p>\u9996\u5148, Kafka \u5E76\u4E0D\u80FD\u5B8C\u5168\u7684\u4FDD\u8BC1\u6D88\u606F\u4E0D\u91CD\u590D.</p><p>0.11 \u7248\u672C\u524D, Kafka \u6709\u4E24\u6B21\u6D88\u606F\u4F20\u9012: Producer \u53D1\u9001\u4FE1\u606F\u7ED9 Kafka, Consumer \u63A5\u53D7\u6765\u81EA Kafka \u7684\u4FE1\u606F.</p><p>\u4E24\u6B21\u4F20\u9012\u90FD\u4F1A\u5F71\u54CD\u6700\u7EC8\u7ED3\u679C, \u4E14\u4E24\u6B21\u4F20\u9012\u90FD\u662F\u7CBE\u786E\u4E00\u6B21, \u6700\u7EC8\u7ED3\u679C\u624D\u662F\u7CBE\u786E\u4E00\u6B21</p><ul><li> Producer \u4F20\u9012\u6D88\u606F\u7684\u5B9E\u73B0\u4E2D\u6307\u5B9A\u4E86\u4E09\u4E2A ACK \u503C, \u5206\u522B\u4E3A <ol><li>-1 / ALL: Leader Broker \u4F1A\u7B49\u5F85\u6D88\u606F\u5199\u5165\u4E14 ISR \u540C\u6B65\u540E\u76F8\u5E94, \u8FD9\u79CD\u5229\u7528 ISR \u5B9E\u73B0\u53EF\u9760, \u4F46\u541E\u5410\u91CF\u4F4E.</li><li>0: Producer \u4E0D\u7406\u4F1A Broker \u7684\u5904\u7406\u7ED3\u679C, \u4E5F\u4E0D\u5904\u7406\u56DE\u8C03, \u6B64\u65F6\u53EA\u4FDD\u8BC1\u9AD8\u541E\u5410.</li><li> 1: \u5373 Kafka \u9ED8\u8BA4\u7684 ACK \u76F8\u5E94\u7801, Leader Broker \u5199\u5165\u6570\u636E\u4FBF\u76F8\u5E94, \u4E0D\u7B49\u5F85 ISR \u540C\u6B65, \u53EA\u8981 Leader Broker \u5728\u7EBF\u5C31\u4E0D\u4F1A\u4E22\u5931\u6570\u636E. </li></ol></li></ul><blockquote><p>\u9ED8\u8BA4\u7684 Producer \u7EA7\u522B\u662F at least once.</p></blockquote><ul><li> Consumer \u6D88\u606F\u4F20\u9012\u9760 Offset \u4FDD\u8BC1. Consumer \u5B9E\u73B0\u4E2D\u53EF\u4EE5\u6307\u5B9A Offset \u884C\u4E3A, \u5373 <code>enable.auto.commit</code>, \u5B9E\u73B0\u6548\u679C\u4E5F\u662F at least once. </li></ul><h4 id="kafka-%E5%A6%82%E4%BD%95%E5%AE%9E%E7%8E%B0%E5%B9%82%E7%AD%89%E6%80%A7">Kafka \u5982\u4F55\u5B9E\u73B0\u5E42\u7B49\u6027</h4><p>0.11 \u7248\u672C\u540E, Kafka \u63A8\u51FA\u6765 Idempotent Producer, \u5B8C\u6210\u4E86\u5E42\u7B49\u6027\u548C\u4E8B\u52A1\u7684\u652F\u6301.</p><p> \u5728\u8FD9\u4E2A\u673A\u5236\u4E2D, Producer \u4F1A\u53D1\u9001\u591A\u6B21\u540C\u6837\u7684\u6D88\u606F, \u800C Broker \u53EA\u4F1A\u5199\u5165\u4E00\u6B21\u6D88\u606F, Broker \u6267\u884C\u4E86\u6D88\u606F\u7F16\u53F7\u53BB\u91CD. \u5E42\u7B49\u4FDD\u8BC1\u4E86\u5355\u4E00\u5206\u533A\u65E0\u91CD\u590D\u6D88\u606F. </p><blockquote><p>\u5728 Producer \u91CC\u8BBE\u7F6E <code>true=enable.idempotent</code></p></blockquote><p> \u591A\u5206\u533A\u65F6, \u4E3A\u4E86\u4FDD\u8BC1\u540C\u6B65\u5199\u5165\u7684\u4E00\u81F4\u6027, \u5F15\u5165\u4E8B\u52A1\u7406\u5FF5, \u8981\u4E48\u5168\u90E8\u5199\u5165, \u8981\u4E48\u5168\u90E8\u56DE\u6EDA. \u4E8B\u52A1\u4FDD\u8BC1\u4E86\u591A\u5206\u533A\u5199\u5165\u6D88\u606F\u7684\u5B8C\u6574\u6027. </p><blockquote><p>\u5728 Producer \u91CC\u8BBE\u7F6E <code>String something = transactional.id</code></p></blockquote><p> \u6B64\u65F6\u7684 Consumer \u7AEF\u6CA1\u6709 Kafka \u81EA\u5E26\u7684\u7B56\u7565\u53BB\u652F\u6301 exactly once \u6D88\u606F\u6A21\u5F0F, \u6240\u4EE5\u9700\u8981\u624B\u6413\u4E00\u4E2A\u903B\u8F91, \u6BD4\u5982\u81EA\u5DF1\u7BA1\u7406 offset \u7684\u63D0\u4EA4. </p><h3 id="kafka-%E9%87%8D%E5%A4%8D%E6%B6%88%E8%B4%B9%E4%B8%8E%E6%BC%8F%E6%B6%88%E8%B4%B9"> Kafka \u91CD\u590D\u6D88\u8D39\u4E0E\u6F0F\u6D88\u8D39 </h3><h4 id="%E9%87%8D%E5%A4%8D%E6%B6%88%E8%B4%B9">\u91CD\u590D\u6D88\u8D39</h4><p>Consumer \u6D88\u8D39\u540E\u6CA1\u6709 commit offset.</p><p>\u901A\u5E38\u7531\u7A0B\u5E8F\u7A81\u7136\u4E0B\u7EBF, \u6D88\u8D39\u8017\u65F6\u957F, \u81EA\u52A8\u63D0\u4EA4 offset \u5931\u8D25.</p><h4 id="%E6%BC%8F%E6%B6%88%E8%B4%B9">\u6F0F\u6D88\u8D39</h4><p>\u5148\u63D0\u4EA4 offset \u540E consume, \u6709\u53EF\u80FD\u9020\u6210\u6570\u636E\u7684\u91CD\u590D.</p><h3 id="kafka-%E6%95%B0%E6%8D%AE%E9%A1%BA%E5%BA%8F%E4%B8%8E%E6%B6%88%E6%81%AF%E9%A1%BA%E5%BA%8F"> Kafka \u6570\u636E\u987A\u5E8F\u4E0E\u6D88\u606F\u987A\u5E8F </h3><h4 id="%E8%A7%A3%E5%86%B3%E6%95%B0%E6%8D%AE%E9%A1%BA%E5%BA%8F">\u89E3\u51B3\u6570\u636E\u987A\u5E8F</h4><ul><li>\u76F8\u540C\u8BA2\u5355\u7684\u6570\u636E\u53D1\u9001\u5230\u540C\u4E00\u5206\u533A.</li><li> \u91C7\u7528 Kafka \u7684 Partitioner \u5206\u533A\u7B56\u7565: <ol><li>\u6307\u5B9A\u6570\u636E\u76EE\u6807\u7684\u53D1\u9001\u5206\u533A.</li><li>\u6839\u636E\u6570\u636E\u7684 key \u83B7\u53D6 hashCode \u8FDB\u884C\u5206\u533A.</li><li>\u8F6E\u8BE2\u5206\u533A.</li><li>\u624B\u52A8\u6307\u5B9A\u5206\u533A.</li></ol></li><li>Flume \u6574\u5408 Kafka \u987A\u5E8F\u6027.</li></ul><h4 id="kafka-%E4%BD%93%E7%8E%B0%E6%B6%88%E6%81%AF%E6%9C%89%E5%BA%8F%E6%80%A7">Kafka \u4F53\u73B0\u6D88\u606F\u6709\u5E8F\u6027</h4><p>\u6BCF\u4E2A\u5206\u533A\u5185\u7684\u6BCF\u6761\u6D88\u606F\u90FD\u6709 offset, \u53EA\u80FD\u4FDD\u8BC1\u5206\u533A\u5185\u6709\u5E8F.</p><p>\u4E3A\u4E86\u4FDD\u8BC1\u6574\u4E2A topic \u6709\u5E8F, \u9700\u8981\u8C03\u6574 partition \u4E3A 1.</p><h3 id="kafka-%E4%B8%BA%E4%BB%80%E4%B9%88%E9%80%9F%E5%BA%A6%E5%BF%AB">Kafka \u4E3A\u4EC0\u4E48\u901F\u5EA6\u5FEB</h3><ul><li> \u9875\u7F13\u5B58\u6280\u672F <ul><li>Kafka \u5728\u64CD\u4F5C\u6570\u636E\u7684\u65F6\u5019\u4F1A\u5199\u5165\u5185\u5B58, \u7531\u64CD\u4F5C\u7CFB\u7EDF\u51B3\u5B9A\u4F55\u65F6\u628A\u5185\u5B58\u7684\u6570\u636E\u5199\u5165\u78C1\u76D8.</li></ul></li><li>\u78C1\u76D8\u987A\u5199, \u5373\u5199\u5165\u6587\u4EF6\u672B\u5C3E\u4FDD\u8BC1\u5199\u5165\u901F\u5EA6.</li><li> Kafka \u4E3A\u4E86\u89E3\u51B3\u5728\u5E94\u7528\u5185\u6570\u636E\u7684\u901A\u8BAF\u635F\u8017, \u5F15\u5165\u4E86\u96F6\u62F7\u8D1D\u6280\u672F, \u5373\u8BFB\u64CD\u4F5C\u7684\u6570\u636E\u8FDB\u5165\u7F13\u5B58\u540E\u53D1\u9001\u7ED9\u7F51\u5361, \u540C\u65F6\u62F7\u8D1D\u63CF\u8FF0\u7B26\u800C\u975E\u6570\u636E\u7ED9 Socket \u7F13\u5B58. </li></ul><h3 id="kafka-%E5%A6%82%E4%BD%95%E6%B8%85%E7%90%86%E8%BF%87%E6%9C%9F%E6%95%B0%E6%8D%AE"> Kafka \u5982\u4F55\u6E05\u7406\u8FC7\u671F\u6570\u636E </h3><p> Kafka \u7684\u65E5\u5FD7\u4FDD\u5B58\u5728 /kafka-logs \u6587\u4EF6\u5939\u4E2D, \u9ED8\u8BA4\u4E03\u5929\u6E05\u7406, \u5F53\u65E5\u5FD7\u6EE1\u8DB3\u5220\u9664\u6761\u4EF6\u65F6, \u4F1A\u88AB\u6807\u6CE8\u4E3A &quot;delete&quot;, \u6B64\u65F6\u6587\u4EF6\u53EA\u662F\u65E0\u6CD5\u88AB\u7D22\u5F15, \u6587\u4EF6\u672C\u8EAB\u4E0D\u4F1A\u88AB\u5220\u9664. \u5F53\u8D85\u8FC7 <code>log.segment.delete.delay.ms</code> \u65F6\u95F4\u540E, \u6587\u4EF6\u4F1A\u88AB\u6587\u4EF6\u7CFB\u7EDF\u5220\u9664. </p><h3 id="kafka-%E8%93%84%E6%B0%B4%E6%B1%A0%E6%9C%BA%E5%88%B6">Kafka \u84C4\u6C34\u6C60\u673A\u5236</h3></div></div>',2),k=E({__name:"kafka",setup(e){return(l,C)=>(B(),F(D,null,[i,A(u)],64))}});export{k as default};
