import{_ as e,e as t}from"./index.f6e50d68.js";const c={},i=t('<title>Kismet Usage</title><div class="container"><div class="content"><h1 id="kismet-%E4%BD%BF%E7%94%A8%E8%AE%B0%E5%BD%95">Kismet \u4F7F\u7528\u8BB0\u5F55</h1><p> \u6700\u8FD1, \u5728\u5DE5\u4F5C\u4E0A\u9047\u5230\u4E86\u4F7F\u7528 <a href="https://www.kismetwireless.net/">kismet</a> \u4F5C\u4E3A web \u670D\u52A1\u5668, \u7528\u4EE5\u76D1\u542C\u7F51\u7EDC, \u6216\u8005\u62D3\u5C55 kismet \u81EA\u5E26\u7684 webUI. </p><h2 id="setup">Setup</h2><p> \u4F7F\u7528 Rock Pi 4c plus \u4F5C\u4E3A\u5F00\u53D1\u677F, \u90E8\u7F72 kismet \u670D\u52A1; \u8DDF\u968F\u6587\u6863\u4ECB\u7ECD, \u6DFB\u52A0 <code>apt</code> \u4ED3\u5E93\u5E76\u4E0B\u8F7D\u4E8C\u8FDB\u5236\u6587\u4EF6. </p><p>\u4F7F\u7528 SUID \u6A21\u5F0F\u64CD\u4F5C\u7F51\u5361, \u907F\u514D Root \u7528\u6237\u542F\u52A8\u5BFC\u81F4\u7684\u5B89\u5168\u95EE\u9898.</p><blockquote><p>\u4E8C\u8FDB\u5236\u6587\u4EF6\u4E0B\u8F7D\u597D\u4F1A\u81EA\u52A8\u914D\u7F6E kismet \u7528\u6237\u7EC4, \u4F46\u662F\u9700\u8981\u624B\u52A8\u52A0\u5165\u7528\u6237, \u7B2C\u4E00\u6B21\u5B89\u88C5\u7684\u65F6\u5019\u5728\u8FD9\u91CC\u8E29\u4E86\u5751.</p></blockquote><p> \u5728 <code>/etc/kismet/kismet.conf</code> \u4E2D\u6DFB\u52A0 <code>Datasource</code>, \u6216\u8005\u6211\u66F4\u559C\u6B22\u4F7F\u7528\u547D\u4EE4 <code>kismet -c Datasource</code> \u6765\u542F\u52A8. </p><p> \u7B2C\u4E00\u6B21\u5B89\u88C5\u597D\u542F\u52A8, \u62A5\u9519\u63D0\u793A\u65E0\u6CD5\u542F\u52A8\u7F51\u5361\u7684\u76D1\u542C\u6A21\u5F0F, \u67E5\u4E86\u4E00\u4E0B\u8D44\u6599\u53D1\u73B0 Rock Pi \u7684\u677F\u8F7D\u7F51\u5361 AzureWave aw-cm256sm \u4E0D\u652F\u6301\u76D1\u542C\u6A21\u5F0F; \u968F\u5373\u627E\u4E86\u4E2A RT 3070 USB \u7F51\u5361, \u5370\u8C61\u91CC\u8FD9\u662F\u4E2A\u514D\u9A71\u52A8, \u6216\u8005\u8BF4 Linux \u81EA\u5E26\u9A71\u52A8\u7684\u7F51\u5361, \u63D2\u677F\u5B50\u4E0A\u5E76\u6CA1\u6709\u8D77\u4F5C\u7528; \u7814\u7A76\u4E00\u901A\u677F\u8F7D\u7CFB\u7EDF <code>Rock Pi 4 Ubuntu Server 20.4 Focal</code>, \u53D1\u73B0\u9A71\u52A8\u662F\u6709\u7684, \u4F46\u662F\u9ED8\u8BA4\u6CA1\u6709\u542F\u7528; \u79FB\u690D\u4E86\u4E00\u4E2A\u9A71\u52A8\u540E, \u8FD8\u8981\u7F16\u8BD1\u4E00\u4E2A\u542F\u7528\u7684\u5185\u6838\u7ED9\u677F\u5B50\u7528\u{1F613}, \u4F46\u662F\u53C8\u6CA1\u627E\u5230\u8FD9\u8D27\u7684\u6E90\u7801, \u968F\u4FBF\u4E0B\u4E86\u4E2A Ubuntu 20.04 \u51D1\u5408\u8C03\u4E86\u8C03\u5C31\u70E7\u7CFB\u7EDF\u8FDB\u53BB\u4E86; \u7136\u540E, \u7136\u540E! \u968F\u4FBF\u627E\u7684\u8FD9\u4E2A\u7CFB\u7EDF\u53C8\u591A\u4E86\u70B9\u4E1C\u897F, \u4F46\u53C8\u5C11\u4E86\u70B9\u4E1C\u897F, \u4ECE\u4E8C\u8FDB\u5236\u6E90\u4E0B\u8F7D\u5B89\u88C5 kismet \u540E, \u7ADF\u7136\u591A\u4E86\u4E2A TUI, \u53C8\u62A5\u9519\u6CA1\u6709 <code>WireShark</code>, \u4E5F\u6CA1\u6CD5\u8FDE\u5230\u672C\u5730\u670D\u52A1\u5668 ( \uFF9F\u2200\u3002). </p><p> \u6298\u817E\u677F\u5B50\u4E00\u661F\u671F, \u81EA\u66B4\u81EA\u5F03\u540E\u628A\u7F51\u5361\u63D2\u5F00\u53D1\u673A\u4E0A\u8DD1 kismet, \u5B9E\u9645\u4F53\u9A8C\u6D41\u7545, \u6293\u4E86\u4E00\u4E0B kismet RESTFul Api \u7684\u8BF7\u6C42\u54CD\u5E94\u6570\u636E, mock \u51FA\u6765\u7ED9\u524D\u7AEF\u7528\u4E86\u{1F60B}. </p><p>\u7136\u800C\u524D\u7AEF\u4E5F\u662F\u6211\u5728\u505A, \u4E4B\u540E\u8BA9\u5176\u4ED6\u7EC4\u5458\u7422\u78E8\u9A71\u52A8\u53BB\u5427 ( \uFF9F\u2200\u3002)</p><h2 id="wip">WIP</h2></div></div>',2);function o(s,p){return i}const a=e(c,[["render",o]]);export{a as default};
