import{B as E}from"./BackToTopButton.4b5a936b.js";import{d as F,o as B,c as D,a as u,b as A,F as C,e as p}from"./index.f6e50d68.js";const s="/images/blogs/flow_z13/1.jpg",o="/images/blogs/flow_z13/2.jpg",t="/images/blogs/flow_z13/3.jpg",e="/images/blogs/flow_z13/4.jpg",i="/images/blogs/flow_z13/5.jpg",a="/images/blogs/flow_z13/6.jpg",_="/images/blogs/flow_z13/7.jpg",r="/images/blogs/flow_z13/8.jpg",c=u("title",null,"z13 \u9510\u8BC4",-1),n={class:"container"},g={class:"content"},l=p('<p><del>\u58F9\u4E07\u2463\u5343\u3007 6\u21699</del> 14069 \u4E70\u7684\u9876\u914D, i9 \u5904\u7406\u5668 \u914D 4060 \u663E\u5361\u7684\u5C0F\u5E73\u677F (\u548C 14 \u5BF8 zenbook \u4E00\u822C\u5927)</p><p>\u89C4\u683C\u53C2\u6570\u53C2\u8003\u5B98\u7F51\u7684.</p><h1 id="%E5%A4%9A%E5%9B%BE%E9%95%BF%E6%96%87%E9%A2%84%E8%AD%A6"><strong>\u591A\u56FE\u957F\u6587\u9884\u8B66</strong></h1><h2 id="%E8%B4%AD%E4%B9%B0%E5%92%8C%E6%94%B6%E8%B4%A7">\u8D2D\u4E70\u548C\u6536\u8D27</h2><p> \u53D1\u552E\u5F53\u665A\u4E0B\u5355\u72D7\u4E1C, \u5728\u89E3\u7981\u4E0B\u5355\u524D 0.2 \u79D2\u6293\u5305\u6536\u5230\u4E86\u4E00\u4E2A\u4E8B\u4EF6, \u628A\u5E93\u5B58\u6E05\u96F6\u4E86, \u6240\u4EE5\u672C\u4EBA\u901A\u8FC7\u808C\u8089\u53CD\u5E94\u5B8C\u5168\u62A2\u4E0D\u8FC7\u811A\u672C, \u867D\u7136\u6211\u4E5F\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u4F46\u6211\u6BD4\u8F83\u61D2, \u6CA1\u6709\u81EA\u7814\u811A\u672C\u53BB\u62A2, \u7ED9\u5404\u4F4D\u7801\u519C\u4E22\u8138\u4E86\u{1F60E} </p><p>\u9042\u524D\u5F80\u5978\u5546\u5B9D\u52A0\u4EF7\u4E0B\u5355;</p><p>\u7136\u540E\u5C31\u662F\u6F2B\u957F\u7684\u4E00\u5929\u96F6\u4E24\u591C\u7684\u6F2B\u6F2B\u957F\u591C\u7B49\u5F85, \u6F2B\u957F, \u5341\u5206\u6F2B\u957F, \u672C\u6025\u6025\u56FD\u738B\u9AD8\u5F3A\u5EA6\u50AC\u5BA2\u670D\u628A\u5BA2\u670D\u90FD\u50AC\u5410\u4E86.</p><p> \u8FD9\u91CC\u6BD4\u8F83\u5F31\u667A\u7684\u4E8B\u60C5\u5C31\u662F\u540C\u57CE\u9001, \u6211\u5728\u5317\u7406\u4E0A\u73ED\u79BB\u4E0A\u5730\u5C31\u4E8C\u5341\u5206\u949F\u8F66\u7A0B, \u4F46\u662F\u7ECF\u9500\u5546\u4E0D\u8BA9\u6211\u52A0\u94B1\u53D1\u95EA\u9001, \u5F3A\u884C\u7B49\u4E86\u4E00\u5BBF\u624D\u914D\u9001. </p><p>\u65E9\u4E0A\u6B63\u505A\u996D\u5462\u5FEB\u9012\u9001\u6765\u4E86.</p><h2 id="%E5%BC%80%E7%AE%B1">\u5F00\u7BB1</h2><p>\u505A\u996D\u770B\u9505\u5462, \u968F\u4FBF\u62C6\u4E86\u62C6\u6CA1\u4EC0\u4E48\u597D\u77A7\u7684.</p><p><img src="'+s+'" alt="\u770B\u770B\u6211\u7684\u7C89\u8272\u56F4\u88D9"></p><h2 id="%E6%80%A7%E8%83%BD%E6%B5%8B%E8%AF%95">\u6027\u80FD\u6D4B\u8BD5</h2><p>\u5206\u522B\u6D4B\u8BD5\u4E86\u6E38\u620F\u6027\u80FD\u548C\u8F6F\u4EF6\u7F16\u8BD1\u6027\u80FD, \u4EE5\u53CA\u5E38\u89C4\u7684\u786C\u4EF6\u8BBE\u5907\u68C0\u67E5.</p><h3 id="%E9%A6%96%E5%85%88%E6%98%AF%E7%A0%8D%E6%98%9F%E8%BD%BD%E4%BA%BA%E5%AF%BC%E5%BC%B9%E7%A0%94%E7%A9%B6%E9%99%A2-2-%E7%9A%84%E6%B5%8B%E8%AF%95"> \u9996\u5148\u662F\u780D\u661F\u8F7D\u4EBA\u5BFC\u5F39\u7814\u7A76\u9662 2 \u7684\u6D4B\u8BD5 </h3><p>\u753B\u8D28\u62C9\u6EE1, 1080p \u5206\u8FA8\u7387; 60 \u5E27\u7545\u73A9</p><p><img src="'+o+'" alt="ksp2"><img src="'+t+'" alt="60 \u5E27\u7545\u73A9"><img src="'+e+'" alt="\u8F7D\u4EBA\u822A\u5929\u5706\u6EE1\u6210\u529F"></p><h3 id="%E5%8E%9F%E5%AD%90%E4%B9%8B%E5%BF%83%E7%9A%84%E6%B5%8B%E8%AF%95">\u539F\u5B50\u4E4B\u5FC3\u7684\u6D4B\u8BD5</h3><p> \u753B\u8D28\u62C9\u6EE1, \u4E0D\u5F00 dlss, 2k \u5206\u8FA8\u7387 <img src="'+i+'" alt="\u539F\u5B50\u4E4B\u5FC3"><img src="'+a+'" alt="\u751A\u81F3\u6709 40fps"></p><p>\u8FD9\u4E2A\u5C0F\u5E73\u677F\u5C5E\u5B9E\u624B\u63D0\u6838\u5F39, \u80FD\u8DD1\u5230 40~50 \u5E27, \u5B8C\u5168\u8D85\u51FA\u4E86\u6211\u7684\u9884\u671F.</p><h2 id="%E5%85%B6%E4%BB%96%E7%A1%AC%E4%BB%B6">\u5176\u4ED6\u786C\u4EF6</h2><p> \u975E\u5168\u8D34\u5408\u5C4F\u5E55, \u79BB\u8FD1\u4E86\u659C\u7740\u80FD\u770B\u5230\u80CC\u5149\u677F: <img src="'+_+'" alt="\u5C4F\u5E55"></p><p> \u81EA\u5E26\u7684\u97F3\u54CD, \u97F3\u8D28\u542C\u8D77\u6765\u975E\u5E38\u4E0D\u9519: <img src="'+r+'" alt="speaker"></p><p>\u5185\u5B58:</p><p> \u56FE\u5FD8\u4F20\u4E86, \u53E3\u8FF0\u4E00\u4E0B\u5C31\u662F \u5185\u5B58 bus \u53EA\u6709\u4E0D\u5230 600Mhz, \u6211\u7684\u4E00\u4F4D\u963F\u82CF\u65AF\u7684\u5DE5\u7A0B\u5E08\u670B\u53CB\u5BF9\u6B64\u9510\u8BC4 &quot;\u592A\u4F4E\u4E86&quot;\u3001&quot;rog \u7528\u7684\u6700\u4F4E\u6863\u4F4D\u6027\u80FD&quot;, \u4ED6\u8BF4\u56FA\u4EF6\u66F4\u65B0\u540E\u80FD\u4FEE\u590D\u8FD9\u4E2A\u62C9\u8DE8\u70B9, \u867D\u7136\u4F46\u662F\u4ED6\u7ED9\u6211\u7684 beta \u7248\u672C\u7684\u56FA\u4EF6\u6211\u5237\u4E0D\u4E0A\u53BB. </p><h2 id="%E7%BC%96%E8%AF%91%E8%BD%AF%E4%BB%B6">\u7F16\u8BD1\u8F6F\u4EF6</h2><p>\u8DD1\u4E86\u4E00\u4E2A nodejs \u548C rust, \u4E5F\u5C31\u662F tauri \u6846\u67B6\u505A\u7684\u8F6F\u4EF6.</p><p> \u56FE\u4E5F\u5FD8\u4F20\u4E86, \u6027\u80FD\u597D\u50CF\u86EE\u4E0D\u9519\u7684, AMD 5800u \u6EE1\u8F7D\u53BB\u7F16\u8BD1\u8981\u4E00\u5206\u949F, \u8FD9\u4E2A i9 \u5E26\u4E0B\u8F7D\u4F9D\u8D56\u548C\u7F16\u8BD1\u5C31\u5341\u79D2\u949F, \u6700\u9AD8\u9891\u7387\u5927\u6838\u4E5F\u8DD1\u5230\u4E86 5.4 GHz. </p><p>\u4E0D\u8FC7\u670B\u53CB\u8BF4\u6253\u6E38\u620F\u7684\u65F6\u5019 cpu \u6CA1\u6709\u8DD1\u6EE1, gpu \u4F1A\u6709\u74F6\u9888, \u7136\u540E\u8FD8\u662F\u8BF4\u90A3\u4E2A\u88C5\u4E0D\u4E0A\u7684\u56FA\u4EF6\u80FD\u4FEE\u590D, \u6211\u8C14\u8C14\u{1F921}.</p><p>\u4E0B\u5468\u5E26\u5230\u516C\u53F8\u914D\u4E00\u4E0B\u786C\u4EF6\u5F00\u53D1\u73AF\u5883, \u5230\u65F6\u5019\u5176\u4ED6\u7684\u6D4B\u8BD5\u7ED3\u679C\u6BD4\u5982\u7F16\u8BD1 rock bsp \u4E4B\u7C7B\u7684\u91CD\u578B\u4EFB\u52A1, \u4F1A\u9644\u8A00\u5230\u672C\u8D34\u4E0B\u9762.</p><p>\u4EE5\u53CA\u53EF\u80FD\u4F1A\u5B58\u5728\u7684 linux \u652F\u6301\u6298\u817E, \u51C6\u5907\u88C5\u4E2A arch + hyprland \u73A9\u73A9.</p><h2 id="%E7%BB%AD%E8%88%AA">\u7EED\u822A</h2><p>54 \u74E6\u65F6\u7684\u7535\u6C60, \u6D4B\u8BD5\u7684\u65F6\u5019\u7535\u91CF\u4E3A 94 %;</p><p>\u5916\u653E\u97F3\u4E50; \u5C4F\u5E55\u4EAE\u5EA6 250 \u5C3C\u7279\u5DE6\u53F3, \u5C4F\u5E55\u5237\u65B0\u7387 60hz; \u952E\u76D8\u706F\u5173\u95ED, \u63A5\u4E86\u4E2A\u9F20\u6807.</p><p>\u72EC\u7ACB\u663E\u5361\u901A\u8FC7\u90A3\u4E2A\u4EC0\u4E48 mux \u6280\u672F\u5173\u95ED\u4E86, \u4E0D\u77E5\u9053\u662F\u771F\u7684\u6CA1\u6709\u4F9B\u7535\u8FD8\u662F\u5047\u88C5\u5173\u6389.</p><p>\u5168\u5C4F\u64AD\u653E 1080p \u89C6\u9891, \u540E\u53F0\u7A7A\u8DD1\u4E86\u4E2A syncing, \u8FD8\u6302\u4E86\u4E00\u4E2A qq.</p><p> \u91CD\u70B9\u6765\u4E86, \u7EED\u822A\u505A\u5230\u4E86\u60CA\u4EBA\u7684 <em><strong>2 \u5C0F\u65F6 40 \u5206\u949F</strong></em>, \u6CA1\u9519\u5C31\u4E0D\u5230\u4E09\u4E2A\u5C0F\u65F6, \u8FD9\u8D27\u5B8C\u5168\u6CA1\u6709\u7EED\u822A. </p><hr><p>\u5404\u4F4D\u6709\u7EED\u822A\u9700\u6C42\u7684\u65C1\u53CB\u8FD8\u662F\u8001\u8001\u5B9E\u5B9E\u4E70\u82F9\u679C\u7535\u8111\u7F62.</p><h6 id="%E5%BD%93%E7%84%B6%E6%88%91%E5%B9%B3%E6%97%B6%E4%B9%9F%E6%98%AF%E5%BC%80%E7%9D%80-80%E7%94%B5%E6%B1%A0%E5%AE%B9%E9%87%8F%E6%A8%A1%E5%BC%8F-%E6%88%91%E6%A0%B9%E6%9C%AC%E6%B2%A1%E6%9C%89%E4%B8%8D%E6%8F%92%E7%94%B5%E8%BF%90%E8%A1%8C%E8%BF%B7%E4%BD%A0%E6%A0%B8%E5%BC%B9%E7%9A%84%E9%9C%80%E6%B1%82"> \u5F53\u7136\u6211\u5E73\u65F6\u4E5F\u662F\u5F00\u7740 80%\u7535\u6C60\u5BB9\u91CF\u6A21\u5F0F, \u6211\u6839\u672C\u6CA1\u6709\u4E0D\u63D2\u7535\u8FD0\u884C\u8FF7\u4F60\u6838\u5F39\u7684\u9700\u6C42 </h6><h2 id="%E5%85%B6%E4%BB%96%E9%94%90%E8%AF%84">\u5176\u4ED6\u9510\u8BC4</h2><p>\u5B9E\u5728\u662F\u8D85\u51FA\u4E86\u6211\u7684\u9884\u6599, \u8FD9\u4E2A\u7535\u8111\u6211\u5BA3\u5E03\u8FD9\u4E8B\u6211\u4E70\u8FC7\u6700\u725B\u903C\u6700\u503C\u5F97\u6700\u5FEB\u4E50\u7684\u6D88\u8D39\u8005\u7535\u5B50\u4EA7\u54C1.</p><p> \u6211\u4E00\u76F4\u8BA4\u4E3A\u8FD9\u6837\u7684\u5E73\u677F\u624D\u662F\u672A\u6765\u4E2A\u4EBA\u6570\u5B57\u52A9\u624B\u7684\u771F\u6B63\u5F62\u6001, \u5728\u56FA\u4F53\u7535\u6C60\u6280\u672F\u6210\u719F\u548C\u5916\u63A5 xgm \u80FD\u7528 pci 5 \u7684\u65F6\u5019, \u5C31\u662F\u8FD9\u7C7B\u8FF7\u4F60\u6838\u5F39\u7684\u6625\u5929. </p><hr><p>\u4ECA\u65E5\u8282\u76EE\u5230\u6B64\u7ED3\u675F, \u611F\u8C22\u5404\u4F4D\u89C2\u770B, \u56DE\u89C1!</p>',45),b=F({__name:"z13",setup(m){return(h,d)=>(B(),D(C,null,[c,u("div",n,[u("div",g,[l,A(E)])])],64))}});export{b as default};
